00079         private async Task StartSearchTask(
00080             ProgressBarModel progressBarModel,
00081             IFileSystemProvider fileSystemProvider,
00082             TextEditorFindAllState textEditorFindAllState,
00083             IDispatcher dispatcher,
00084             CancellationToken cancellationToken)
00085         {
00086             var filesProcessedCount = 0;
00087             var textSpanList = new List<TextEditorTextSpan>();
00088             var searchException = (Exception?)null;
00089             
00090             try
00091             {
00092                 ShowFilesProcessedCountOnUi(0);
00093                 await RecursiveSearch(textEditorFindAllState.StartingDirectoryPath);
00094             }
00095             catch (Exception e)
00096             {
00097                 searchException = e;
00098             }
00099             finally
00100             {
00101                 dispatcher.Dispatch(new FlushSearchResultsAction(textSpanList));
00102             
00103                 if (searchException is null)
00104                 {
00105                     ShowFilesProcessedCountOnUi(1, true);
00106                 }
00107                 else
00108                 {
00109                     progressBarModel.SetProgress(
00110                         progressBarModel.DecimalPercentProgress,
00111                         searchException.ToString());
00112                         
00113                     progressBarModel.Dispose();
00114                     // The use of '_textEditorFindAllStateWrap.Value' is purposeful.
00115                     ConstructTreeView(_textEditorFindAllStateWrap.Value);
00116                     dispatcher.Dispatch(new SetProgressBarModelAction(progressBarModel));
00117                 }
00118             }
