00120             async Task RecursiveSearch(string directoryPath)
00121             {
00122                 cancellationToken.ThrowIfCancellationRequested();
00123                 
00124                 // Considering the use a breadth first algorithm
00125     
00126                 // Search Files
00127                 {
00128                     var childFileList = await fileSystemProvider.Directory
00129                         .GetFilesAsync(directoryPath)
00130                         .ConfigureAwait(false);
00131         
00132                     foreach (var childFile in childFileList)
00133                     {
00134                         // TODO: Don't hardcode file extensions here to avoid searching through them.
00135                         //       Reason being, hardcoding them isn't going to work well as a long term solution.
00136                         //       How does one detect if a file is not text?
00137                         //       |
00138                         //       I seem to get away with opening some non-text files, but I think a gif I opened
00139                         //       had 1 million characters in it? So this takes 2 million bytes in a 2byte char?
00140                         //       I'm not sure exactly what happened, I opened the gif and the app froze,
00141                         //       I saw the character only at a glance. (2024-07-20)
00142                         if (!childFile.EndsWith(".jpg") &&
00143                             !childFile.EndsWith(".png") &&
00144                             !childFile.EndsWith(".pdf") &&
00145                             !childFile.EndsWith(".gif"))
00146                         {
00147                             await PerformSearchFile(childFile).ConfigureAwait(false);
00148                         }
00149                         
00150                         filesProcessedCount++;
00151                         ShowFilesProcessedCountOnUi(0);
00152                     }
00153                 }
00154         
00155                 // Recurse into subdirectories
00156                 {
00157                     var subdirectoryList = await fileSystemProvider.Directory
00158                         .GetDirectoriesAsync(directoryPath)
00159                         .ConfigureAwait(false);
00160         
00161                     foreach (var subdirectory in subdirectoryList)
00162                     {
00163                         if (subdirectory.Contains(".git") ||
00164                             subdirectory.Contains(".vs") ||
00165                             subdirectory.Contains(".vscode") ||
00166                             subdirectory.Contains(".idea") ||
00167                             subdirectory.Contains("bin") ||
00168                             subdirectory.Contains("obj"))
00169                         {
00170                             continue;
00171                         }
00172         
00173                         await RecursiveSearch(subdirectory).ConfigureAwait(false);
00174                     }
00175                 }
00176             }
