00178             async Task PerformSearchFile(string filePath)
00179             {
00180                 var text = await fileSystemProvider.File.ReadAllTextAsync(filePath).ConfigureAwait(false);
00181                 var query = textEditorFindAllState.SearchQuery;
00182                     
00183                 var matchedTextSpanList = new List<TextEditorTextSpan>();
00184         
00185                 for (int outerI = 0; outerI < text.Length; outerI++)
00186                 {
00187                     if (outerI + query.Length <= text.Length)
00188                     {
00189                         int innerI = 0;
00190                         for (; innerI < query.Length; innerI++)
00191                         {
00192                             if (text[outerI + innerI] != query[innerI])
00193                                 break;
00194                         }
00195         
00196                         if (innerI == query.Length)
00197                         {
00198                             // Then the entire query was matched
00199                             matchedTextSpanList.Add(new TextEditorTextSpan(
00200                                 outerI,
00201                                 outerI + innerI,
00202                                 (byte)FindOverlayDecorationKind.LongestCommonSubsequence,
00203                                 new ResourceUri(filePath),
00204                                 text));
00205                         }
00206                     }
00207                 }
00208         
00209                 foreach (var matchedTextSpan in matchedTextSpanList)
00210                 {
00211                     textSpanList.Add(matchedTextSpan);
00212                 }
00213             }
