00236         private void ConstructTreeView(TextEditorFindAllState textEditorFindAllState)
00237         {
00238             var groupedResults = textEditorFindAllState.SearchResultList.GroupBy(x => x.ResourceUri);
00239         
00240             var treeViewList = groupedResults.Select(group =>
00241             {
00242                 var absolutePath = _environmentProvider.AbsolutePathFactory(
00243                     group.Key.Value,
00244                     false);
00245                     
00246                 return (TreeViewNoType)new TreeViewFindAllGroup(
00247                     group.Select(textSpan => new TreeViewFindAllTextSpan(
00248                         textSpan,
00249                         absolutePath,
00250                         false,
00251                         false)).ToList(),
00252                     absolutePath,
00253                     true,
00254                     false);
00255             }).ToArray();
00256         
00257             var adhocRoot = TreeViewAdhoc.ConstructTreeViewAdhoc(treeViewList);
00258             var firstNode = treeViewList.FirstOrDefault();
00259         
00260             var activeNodes = firstNode is null
00261                 ? Array.Empty<TreeViewNoType>()
00262                 : new[] { firstNode };
00263         
00264             if (!_treeViewService.TryGetTreeViewContainer(TextEditorFindAllState.TreeViewFindAllContainerKey, out _))
00265             {
00266                 _treeViewService.RegisterTreeViewContainer(new TreeViewContainer(
00267                     TextEditorFindAllState.TreeViewFindAllContainerKey,
00268                     adhocRoot,
00269                     activeNodes.ToImmutableList()));
00270             }
00271             else
00272             {
00273                 _treeViewService.SetRoot(TextEditorFindAllState.TreeViewFindAllContainerKey, adhocRoot);
00274         
00275                 _treeViewService.SetActiveNode(
00276                     TextEditorFindAllState.TreeViewFindAllContainerKey,
00277                     firstNode,
00278                     true,
00279                     false);
00280             }
00281         }
