00042     public Key<KeymapLayer> GetLayer(bool hasSelection)
00043     {
00044         switch (ActiveVimMode)
00045         {
00046             case VimMode.Normal:
00047                 return TextEditorKeymapVimFacts.NormalLayer.Key;
00048             case VimMode.Insert:
00049                 return TextEditorKeymapVimFacts.InsertLayer.Key;
00050             case VimMode.Visual:
00051                 return TextEditorKeymapVimFacts.VisualLayer.Key;
00052             case VimMode.VisualLine:
00053                 return TextEditorKeymapVimFacts.VisualLineLayer.Key;
00054             case VimMode.Command:
00055                 return TextEditorKeymapVimFacts.CommandLayer.Key;
00056             default:
00057                 throw new LuthetusTextEditorException($"The {nameof(ActiveVimMode)}: '{ActiveVimMode}' was not recognized.");
00058         }
00059     }
00060 
00061     public string GetCursorCssClassString()
00062     {
00063         switch (ActiveVimMode)
00064         {
00065             case VimMode.Normal:
00066             case VimMode.Visual:
00067             case VimMode.VisualLine:
00068                 return TextCursorKindFacts.BlockCssClassString;
00069             default:
00070                 return string.Empty;
00071         }
00072     }
00073 
00074     public string GetCursorCssStyleString(
00075         TextEditorModel textEditorModel,
00076         TextEditorViewModel textEditorViewModel,
00077         TextEditorOptions textEditorOptions)
00078     {
00079         var characterWidthInPixels = textEditorViewModel.CharAndLineMeasurements.CharacterWidth;
00080 
00081         switch (ActiveVimMode)
00082         {
00083             case VimMode.Normal:
00084             case VimMode.Visual:
00085             case VimMode.VisualLine:
00086                 {
00087                     var characterWidthInPixelsInvariantCulture = characterWidthInPixels.ToCssValue();
00088                     return $"width: {characterWidthInPixelsInvariantCulture}px;";
00089                 }
00090         }
00091 
00092         return string.Empty;
00093     }
