00838     private void AddInsertLayer()
00839     {
00840         var commandDisplayName = "Vim::Escape";
00841 
00842         var escapeCommand = new TextEditorCommand(
00843             commandDisplayName, "vim-Escape", false, true, TextEditKind.None, null,
00844             interfaceCommandArgs =>
00845             {
00846                 var commandArgs = (TextEditorCommandArgs)interfaceCommandArgs;
00847                     
00848                 var modelModifier = commandArgs.EditContext.GetModelModifier(commandArgs.ModelResourceUri);
00849                 var viewModelModifier = commandArgs.EditContext.GetViewModelModifier(commandArgs.ViewModelKey);
00850                 var cursorModifierBag = commandArgs.EditContext.GetCursorModifierBag(viewModelModifier?.ViewModel);
00851                 var primaryCursorModifier = commandArgs.EditContext.GetPrimaryCursorModifier(cursorModifierBag);
00852 
00853                 ActiveVimMode = VimMode.Normal;
00854                 _ = commandArgs.EditContext.GetViewModelModifier(commandArgs.ViewModelKey);
00855                 return Task.CompletedTask;
00856             });
00857 
00858         _map.Add(new KeymapArgs()
00859         {
00860             Key = "Escape",
00861             Code = "Escape",
00862             LayerKey = TextEditorKeymapVimFacts.InsertLayer.Key
00863         }, escapeCommand);
00864     }
