00944     private TextEditorCommand BuildCommandTextEditor(KeymapArgs keymapArgument, string displayName, string internalIdentifier)
00945     {
00946         return new TextEditorCommand(
00947             displayName, internalIdentifier, false, true, TextEditKind.None, null,
00948             interfaceCommandArgs =>
00949             {
00950                 var commandArgs = (TextEditorCommandArgs)interfaceCommandArgs;
00951                 
00952                 var modelModifier = commandArgs.EditContext.GetModelModifier(commandArgs.ModelResourceUri);
00953                 var viewModelModifier = commandArgs.EditContext.GetViewModelModifier(commandArgs.ViewModelKey);
00954                 var cursorModifierBag = commandArgs.EditContext.GetCursorModifierBag(viewModelModifier?.ViewModel);
00955                 var primaryCursorModifier = commandArgs.EditContext.GetPrimaryCursorModifier(cursorModifierBag);
00956 
00957                 if (modelModifier is null || viewModelModifier is null || cursorModifierBag is null || primaryCursorModifier is null)
00958                     return Task.CompletedTask;
00959 
00960                 var success = VimSentence.TryLex(this, keymapArgument, TextEditorSelectionHelper.HasSelectedText(primaryCursorModifier), out var lexCommand);
00961 
00962                 if (success && lexCommand is not null)
00963                     return lexCommand.CommandFunc.Invoke(commandArgs);
00964 
00965                 return Task.CompletedTask;
00966             });
00967     }
00968 
00969     private TextEditorCommand BuildMovementCommand(KeymapArgs keymapArgs, string key)
00970     {
00971         var commandDisplayName = $"Vim::{key}";
00972 
00973         return new TextEditorCommand(
00974             commandDisplayName, $"vim-{key}", false, true, TextEditKind.None, null,
00975             interfaceCommandArgs =>
00976             {
00977                 var commandArgs = (TextEditorCommandArgs)interfaceCommandArgs;
00978                     
00979                 var modelModifier = commandArgs.EditContext.GetModelModifier(commandArgs.ModelResourceUri);
00980                 var viewModelModifier = commandArgs.EditContext.GetViewModelModifier(commandArgs.ViewModelKey);
00981                 var cursorModifierBag = commandArgs.EditContext.GetCursorModifierBag(viewModelModifier?.ViewModel);
00982                 var primaryCursorModifier = commandArgs.EditContext.GetPrimaryCursorModifier(cursorModifierBag);
00983 
00984                 if (ActiveVimMode == VimMode.Visual || ActiveVimMode == VimMode.VisualLine)
00985                 {
00986                     keymapArgs = keymapArgs with
00987                     {
00988                         ShiftKey = true
00989                     };
00990                 }
00991 
00992                 TextEditorCommand? modifiedCommand = null;
00993 
00994                 var success = _textEditorKeymapDefault.MapFirstOrDefault(keymapArgs, out var command);
00995                 modifiedCommand = command as TextEditorCommand;
00996 
00997                 if (modifiedCommand is null)
00998                 {
00999                     modifiedCommand = new TextEditorCommand(
01000                         "MoveCursor", "MoveCursor", false, true, TextEditKind.None, null,
01001                         interfaceCommandArgs =>
01002                         {
01003                             var commandArgs = (TextEditorCommandArgs)interfaceCommandArgs;
01004                             
01005                             var modelModifier = commandArgs.EditContext.GetModelModifier(commandArgs.ModelResourceUri);
01006                             var viewModelModifier = commandArgs.EditContext.GetViewModelModifier(commandArgs.ViewModelKey);
01007                             var cursorModifierBag = commandArgs.EditContext.GetCursorModifierBag(viewModelModifier?.ViewModel);
01008                             var primaryCursorModifier = commandArgs.EditContext.GetPrimaryCursorModifier(cursorModifierBag);
01009                 
01010                             if (modelModifier is null || viewModelModifier is null || cursorModifierBag is null || primaryCursorModifier is null)
01011                                 return Task.CompletedTask;
01012 
01013                             commandArgs.TextEditorService.ViewModelApi.MoveCursor(
01014                                 new KeymapArgs
01015                                 {
01016                                     Key = key,
01017                                     ShiftKey = keymapArgs.ShiftKey,
01018                                     CtrlKey = keymapArgs.CtrlKey
01019                                 },
01020                                 commandArgs.EditContext,
01021                                 modelModifier,
01022                                 viewModelModifier,
01023                                 cursorModifierBag);
01024                             return Task.CompletedTask;
01025                         });
01026 
01027                     TextEditorCommand finalCommand = modifiedCommand;
01028 
01029                     if (ActiveVimMode == VimMode.Visual)
01030                         finalCommand = TextEditorCommandVimFacts.Motions.GetVisualFactory(modifiedCommand, $"{nameof(TextEditorKeymapVim)}");
01031 
01032                     if (ActiveVimMode == VimMode.VisualLine)
01033                         finalCommand = TextEditorCommandVimFacts.Motions.GetVisualLineFactory(modifiedCommand, $"{nameof(TextEditorKeymapVim)}");
01034 
01035                     return finalCommand.CommandFunc.Invoke(commandArgs);
01036                 }
01037 
01038                 return Task.CompletedTask;
01039             });
01040     }
01041 
01042     private void BuildModifiedMovementCommandsFromUnmodified(KeymapArgs keymapArgument, string key)
01043     {
01044         // Unmodified
01045         {
01046             var unmodifiedKeymapArgument = keymapArgument;
01047 
01048             var arrowLeftCommand = BuildMovementCommand(unmodifiedKeymapArgument, key);
01049             _map.Add(unmodifiedKeymapArgument, arrowLeftCommand);
01050         }
01051 
01052         // Shift
01053         {
01054             var shiftKeymapArgument = keymapArgument with
01055             {
01056                 ShiftKey = true
01057             };
01058 
01059             var arrowLeftCommand = BuildMovementCommand(shiftKeymapArgument, key);
01060             _map.Add(shiftKeymapArgument, arrowLeftCommand);
01061         }
01062 
01063         // Ctrl
01064         {
01065             var ctrlKeymapArgument = keymapArgument with
01066             {
01067                 CtrlKey = true
01068             };
01069 
01070             var arrowLeftCommand = BuildMovementCommand(ctrlKeymapArgument, key);
01071             _map.Add(ctrlKeymapArgument, arrowLeftCommand);
01072         }
01073 
01074         // Shift + Ctrl
01075         {
01076             var shiftCtrlKeymapArgument = keymapArgument with
01077             {
01078                 ShiftKey = true,
01079                 CtrlKey = true
01080             };
01081 
01082             var arrowLeftCommand = BuildMovementCommand(shiftCtrlKeymapArgument, key);
01083             _map.Add(shiftCtrlKeymapArgument, arrowLeftCommand);
01084         }
01085     }
