00245     public void ApplyDecorationRange(
00246         ITextEditorEditContext editContext,
00247         TextEditorModelModifier modelModifier,
00248         IEnumerable<TextEditorTextSpan> textSpans)
00249     {
00250         var localRichCharacterList = modelModifier.RichCharacterList;
00251 
00252         var positionsPainted = new HashSet<int>();
00253 
00254         foreach (var textEditorTextSpan in textSpans)
00255         {
00256             for (var i = textEditorTextSpan.StartingIndexInclusive; i < textEditorTextSpan.EndingIndexExclusive; i++)
00257             {
00258                 if (i < 0 || i >= localRichCharacterList.Length)
00259                     continue;
00260 
00261                 modelModifier.__SetDecorationByte(i, textEditorTextSpan.DecorationByte);
00262                 positionsPainted.Add(i);
00263             }
00264         }
00265 
00266         for (var i = 0; i < localRichCharacterList.Length - 1; i++)
00267         {
00268             if (!positionsPainted.Contains(i))
00269             {
00270                 // DecorationByte of 0 is to be 'None'
00271                 modelModifier.__SetDecorationByte(i, 0);
00272             }
00273         }
00274         
00275         modelModifier.ShouldReloadVirtualizationResult = true;
00276     }
00277 
00278     public void ApplySyntaxHighlighting(
00279         ITextEditorEditContext editContext,
00280         TextEditorModelModifier modelModifier)
00281     {
00282         var syntacticTextSpansList = modelModifier.CompilerService.GetTokenTextSpansFor(modelModifier.ResourceUri);
00283         var symbolsList = modelModifier.CompilerService.GetSymbolsFor(modelModifier.ResourceUri);
00284 
00285         var symbolTextSpansList = symbolsList.Select(s => s.TextSpan);
00286 
00287         var textSpanList = new List<TextEditorTextSpan>();
00288         textSpanList.AddRange(syntacticTextSpansList);
00289         textSpanList.AddRange(symbolTextSpansList);
00290 
00291         ApplyDecorationRange(
00292             editContext,
00293             modelModifier,
00294             textSpanList);
00295     }
