00086                     cursorModifierBag.ViewModelKey,
00087                     new List<TextEditorCursorModifier> { cursor });
00088 
00089                 var valueToInsert = keymapArgs.Key.First().ToString();
00090 
00091                 if (keymapArgs.Code == KeyboardKeyFacts.WhitespaceCodes.ENTER_CODE)
00092                 {
00093                     valueToInsert = LineEndKindPreference.AsCharacters();
00094                     
00095                     // GOAL: Match indentation on newline keystroke (2024-07-04)
00096                     var line = this.GetLineInformation(cursor.LineIndex);
00097 
00098                     var cursorPositionIndex = line.StartPositionIndexInclusive + cursor.ColumnIndex;
00099                     var indentationPositionIndex = line.StartPositionIndexInclusive;
00100 
00101                     var indentationBuilder = new StringBuilder();
00102 
