00104                     {
00105                         var possibleIndentationChar = RichCharacterList[indentationPositionIndex++].Value;
00106 
00107                         if (possibleIndentationChar == '\t' || possibleIndentationChar == ' ')
00108                             indentationBuilder.Append(possibleIndentationChar);
00109                         else
00110                             break;
00111                     }
00112 
