00393             {
00394                 var mostRecentEditInsertBatch = (TextEditorEditInsertBatch)mostRecentEdit;
00395     
00396                 // Only batch if consecutive, and contiguous.
00397                 if (positionIndex == mostRecentEditInsertBatch.PositionIndex + mostRecentEditInsertBatch.ContentBuilder.Length)
00398                 {
00399                     mostRecentEditInsertBatch.ContentBuilder.Append(content);
00400                     return;
00401                 }
00402             }
