00616                         // TODO: How does one handle the 'undo limit'...
00617                         //       ...with respect to 'other' edits?
00618                         //       If one does an 'other edit' with more child edits than
00619                         //       the amount of undo history one can have.
00620                         //
00621                         //       Then it would be impossible
00622                         //       to handle that 'other edit' properly.
00623                         //
00624                         //       Furthermore, one could have a small 'other edit' yet,
00625                         //       by way of future edits moving the undo history,
00626                         //       the 'other edit' opening will be lost.
00627                         break;
00628                     }
00629 
00630                     mostRecentEdit = _editBlocksList[_editBlockIndex.Value];
00631 
00632                     if (mostRecentEdit.EditKind == TextEditorEditKind.Other)
00633                     {
00634                         var mostRecentEditOther = (TextEditorEditOther)mostRecentEdit;
00635     
00636                         // Nothing needs to be done when the tags don't match
00637                         // other than continuing the while loop.
00638                         //
00639                         // Given that the 'CloseOtherEdit(...)'
00640                         // will throw an exception when attempting to close a mismatching other edit.
00641                         //
00642                         // Finding the opening to the child 'other edit' is irrelevant since it is encompassed
00643                         // within the parent.
00644                         if (mostRecentEditOther.Tag == (((TextEditorEditOther)undoEdit).Tag))
00645                         {
00646                             // Need to go one further than the opening,
00647                             _editBlockIndex--;
00648                             break;
00649                         }
00650                     }
00651                     else
00652                     {
00653                         UndoEdit();
00654                     }
00655                 }
00656                 break;
00657             default:
00658                 throw new NotImplementedException($"The {nameof(TextEditorEditKind)}: {undoEdit.EditKind} was not recognized.");
00659         }
00660     }
00661 
00662     public void RedoEdit()
00663     {
00664         // Any modified state needs to be 'null coallesce assigned' to the existing TextEditorModel's value. When reading state, if the state had been 'null coallesce assigned' then the field will be read. Otherwise, the existing TextEditorModel's value will be read.
00665         {
00666             _editBlocksList ??= _textEditorModel.EditBlockList.ToList();
00667             _editBlockIndex ??= _textEditorModel.EditBlockIndex;
00668         }
00669 
00670         // If there is no next then throw exception
00671         if (_editBlockIndex >= _editBlocksList.Count - 1)
00672             throw new LuthetusTextEditorException("No edits are available to perform 'redo' on");
00673 
00674         _editBlockIndex++;
00675         var redoEdit = _editBlocksList[_editBlockIndex.Value];
00676 
00677         switch (redoEdit.EditKind)
00678         {
00679             case TextEditorEditKind.Insert:
00680                 var insertEdit = (TextEditorEditInsert)redoEdit;
00681                 PerformInsert(insertEdit.PositionIndex, insertEdit.Content);
00682                 break;
00683             case TextEditorEditKind.InsertBatch:
00684                 var insertBatchEdit = (TextEditorEditInsertBatch)redoEdit;
00685                 PerformInsert(insertBatchEdit.PositionIndex, insertBatchEdit.ContentBuilder.ToString());
00686                 break;
00687             case TextEditorEditKind.Backspace:
00688                 var backspaceEdit = (TextEditorEditBackspace)redoEdit;
00689                 PerformBackspace(backspaceEdit.PositionIndex, backspaceEdit.Count);
00690                 break;
00691             case TextEditorEditKind.BackspaceBatch:
00692                 var backspaceBatchEdit = (TextEditorEditBackspaceBatch)redoEdit;
00693                 PerformBackspace(backspaceBatchEdit.PositionIndex, backspaceBatchEdit.Count);
00694                 break;
00695             case TextEditorEditKind.Delete: 
00696                 var deleteEdit = (TextEditorEditDelete)redoEdit;
00697                 PerformDelete(deleteEdit.PositionIndex, deleteEdit.Count);
00698                 break;
00699             case TextEditorEditKind.DeleteBatch: 
00700                 var deleteBatchEdit = (TextEditorEditDeleteBatch)redoEdit;
00701                 PerformDelete(deleteBatchEdit.PositionIndex, deleteBatchEdit.Count);
00702                 break;
00703             case TextEditorEditKind.Other:
00704                 while (true)
00705                 {
00706                     if (_editBlockIndex >= _editBlocksList.Count - 1)
00707                     {
00708                         // The 'Redo()' method deals with the next-edit
00709                         // as opposed to the 'Undo()' method that deals with the current-edit
00710                         //
00711                         // Therefore, if there is no 'next-edit' then break out
00712                         break;
00713                     }
00714 
00715                     var nextEdit = _editBlocksList[_editBlockIndex.Value + 1];
00716 
00717                     if (nextEdit.EditKind == TextEditorEditKind.Other)
00718                     {
00719                         var nextEditOther = (TextEditorEditOther)nextEdit;
00720 
00721                         // Regardless of the tag of the next edit. One will need to increment EditIndex.
00722                         _editBlockIndex++;
00723 
00724                         if (nextEditOther.Tag == (((TextEditorEditOther)redoEdit).Tag))
00725                             break;
00726                     }
00727                     else
00728                     {
00729                         RedoEdit();
00730                     }
00731                 }
00732                 break;
00733             default:
00734                 throw new NotImplementedException($"The {nameof(TextEditorEditKind)}: {redoEdit.EditKind} was not recognized.");
00735         }
00736     }
00737 }
