00644         // Reposition the Tab(s)
00645         {
00646             var firstTabKeyPositionIndexToModify = _tabKeyPositionsList.FindIndex(x => x >= positionIndex);
00647 
00648             if (firstTabKeyPositionIndexToModify != -1)
00649             {
00650                 for (var i = firstTabKeyPositionIndexToModify; i < TabKeyPositionList.Count; i++)
00651                 {
00652                     TabKeyPositionList[i] -= charCount;
00653                 }
00654             }
00655         }
00656 
00657         // Reposition the PresentationModel(s)
00658         {
00659             var textSpanForInsertion = new TextEditorTextSpan(
00660                 positionIndex,
00661                 positionIndex + charCount,
00662                 0,
00663                 ResourceUri.Empty,
00664                 string.Empty);
00665 
00666             var textModification = new TextEditorTextModification(false, textSpanForInsertion);
00667 
00668             foreach (var presentationModel in PresentationModelList)
00669             {
00670                 presentationModel.CompletedCalculation?.TextModificationsSinceRequestList.Add(textModification);
00671                 presentationModel.PendingCalculation?.TextModificationsSinceRequestList.Add(textModification);
00672             }
00673         }
00674 
00675         // Delete metadata
00676         {
00677             if (lineEndPositionLazyRemoveRange.index is not null)
00678             {
00679                 _lineEndList.RemoveRange(
00680                     lineEndPositionLazyRemoveRange.index.Value,
00681                     lineEndPositionLazyRemoveRange.count);
00682             }
00683 
00684             if (tabPositionLazyRemoveRange.index is not null)
00685             {
00686                 _tabKeyPositionsList.RemoveRange(
00687                     tabPositionLazyRemoveRange.index.Value,
00688                     tabPositionLazyRemoveRange.count);
00689             }
00690         }
00691 
00692         if (deleteKind == DeleteKind.Delete)
00693         {
00694             // Reposition the cursor
00695             {
00696                 var (lineIndex, columnIndex) = this.GetLineAndColumnIndicesFromPositionIndex(positionIndex);
00697                 cursorModifier.LineIndex = lineIndex;
00698                 cursorModifier.SetColumnIndexAndPreferred(columnIndex);
00699             }
00700 
00701             return (positionIndex, charCount);
00702         }
00703         else if (deleteKind == DeleteKind.Backspace)
00704         {
00705             var calculatedPositionIndex = positionIndex - charCount;
00706 
00707             // Reposition the cursor
00708             {
00709                 var (lineIndex, columnIndex) = this.GetLineAndColumnIndicesFromPositionIndex(calculatedPositionIndex);
00710                 cursorModifier.LineIndex = lineIndex;
00711                 cursorModifier.SetColumnIndexAndPreferred(columnIndex);
00712             }
00713 
00714             return (calculatedPositionIndex, charCount);
00715         }
00716         else
00717         {
00718             throw new NotImplementedException();
00719         }
00720     }
00721 
00722     private void DeleteValue(int positionIndex, int count, CancellationToken cancellationToken)
00723     {
00724         // If cursor is out of bounds then continue
00725         if (positionIndex >= _richCharacterList.Length)
00726             return;
00727 
00728         __RemoveRange(positionIndex, count);
00729     }
00730 }
