00008     public void __Insert(int globalPositionIndex, RichCharacter richCharacter)
00009     {
00010         int indexOfPartitionWithAvailableSpace = -1;
00011         int relativePositionIndex = -1;
00012         var runningCount = 0;
00013 
00014         for (int i = 0; i < _partitionList.Count; i++)
00015         {
00016             TextEditorPartition? partition = _partitionList[i];
00017 
00018             if (runningCount + partition.Count >= globalPositionIndex)
00019             {
00020                 // This is the partition we want to modify.
00021                 // But, we must first check if it has available space.
00022                 if (partition.Count >= PartitionSize)
00023                 {
00024                     __SplitIntoTwoPartitions(i);
00025                     i--;
00026                     continue;
00027                 }
00028 
00029                 relativePositionIndex = globalPositionIndex - runningCount;
00030                 indexOfPartitionWithAvailableSpace = i;
00031                 break;
00032             }
00033             else
00034             {
00035                 runningCount += partition.Count;
00036             }
00037         }
00038 
00039         if (indexOfPartitionWithAvailableSpace == -1)
00040             throw new LuthetusTextEditorException("if (indexOfPartitionWithAvailableSpace == -1)");
00041 
00042         if (relativePositionIndex == -1)
00043             throw new LuthetusTextEditorException("if (relativePositionIndex == -1)");
00044 
00045         var inPartition = _partitionList[indexOfPartitionWithAvailableSpace];
00046         var outPartition = inPartition.Insert(relativePositionIndex, richCharacter);
00047 
00048         _partitionList = _partitionList.SetItem(
00049             indexOfPartitionWithAvailableSpace,
00050             outPartition);
00051     }
