00183     private void __SplitIntoTwoPartitions(int partitionIndex)
00184     {
00185         var originalPartition = _partitionList[partitionIndex];
00186 
00187         var firstUnevenSplit = PartitionSize / 2 + PartitionSize % 2;
00188         var secondUnevenSplit = PartitionSize / 2;
00189 
00190         // Validate multi-byte characters go on same partition (i.e.: '\r\n')
00191         {
00192             // firstUnevenSplit is a count so -1 to make it an index
00193             if (originalPartition.RichCharacterList[firstUnevenSplit - 1].Value == '\r')
00194             {
00195                 if (originalPartition.RichCharacterList[(firstUnevenSplit - 1) + 1].Value == '\n')
00196                 {
00197                     firstUnevenSplit += 1;
00198                     secondUnevenSplit -= 1;
00199                 }
00200             }
00201 
00202             // TODO: If the partition to split ends in '\r' and the cause for the split
00203             //       is to create space in order to insert a '\n',
00204             //       |
00205             //       Then this works out as a "happy accident" of sorts.
00206             //       This is not ideal, it should be more concrete than "oops it worked".
00207             //       |
00208             //       The reason it works is because a split won't check if the next partition
00209             //       has space (? source needed) and will always move the '\r' to the new partition,
00210             //       then return to the insert and put the '\n' immediately after.
00211 
00212             // One of the reasons for not having a multi-byte character span multiple partitions,
00213             // is that if a partition has capacity 4,096 but a count of 2,048,
00214             // one cannot insert between the bytes of a multi-byte character
00215             // so the first partition with only half its capacity used, would be unable to be used
00216             // any further than 2,048 because it would mean writing between its multi-byte character
00217             // than spans into the next partition.
00218         }
00219 
00220         // Replace old
00221         {
00222             var partition = new TextEditorPartition(originalPartition.RichCharacterList
00223                 .Skip(0)
00224                 .Take(firstUnevenSplit)
00225                 .ToList());
00226 
00227             _partitionList = _partitionList.SetItem(
00228                 partitionIndex,
00229                 partition);
00230         }
00231 
00232         // Insert new
00233         {
00234             var partition = new TextEditorPartition(originalPartition.RichCharacterList
00235                 .Skip(firstUnevenSplit)
00236                 .Take(secondUnevenSplit)
00237                 .ToList());
00238 
00239             _partitionList = _partitionList.Insert(
00240                 partitionIndex + 1,
00241                 partition);
00242         }
00243     }
