00245     public void __InsertRange(int globalPositionIndex, IEnumerable<RichCharacter> richCharacterList)
00246     {
00247         var richCharacterEnumerator = richCharacterList.GetEnumerator();
00248 
00249         while (richCharacterEnumerator.MoveNext())
00250         {
00251             int indexOfPartitionWithAvailableSpace = -1;
00252             int relativePositionIndex = -1;
00253             var runningCount = 0;
00254             TextEditorPartition? partition;
00255 
00256             for (int i = 0; i < _partitionList.Count; i++)
00257             {
00258                 partition = _partitionList[i];
00259 
00260                 if (runningCount + partition.Count >= globalPositionIndex)
00261                 {
00262                     if (partition.Count >= PartitionSize)
00263                     {
00264                         __SplitIntoTwoPartitions(i);
00265                         i--;
00266                         continue;
00267                     }
00268 
00269                     relativePositionIndex = globalPositionIndex - runningCount;
00270                     indexOfPartitionWithAvailableSpace = i;
00271                     break;
00272                 }
00273                 else
00274                 {
00275                     runningCount += partition.Count;
00276                 }
00277             }
00278 
00279             if (indexOfPartitionWithAvailableSpace == -1)
00280                 throw new LuthetusTextEditorException("if (indexOfPartitionWithAvailableSpace == -1)");
00281 
00282             if (relativePositionIndex == -1)
00283                 throw new LuthetusTextEditorException("if (relativePositionIndex == -1)");
00284 
00285             partition = _partitionList[indexOfPartitionWithAvailableSpace];
00286             var partitionAvailableSpace = PartitionSize - partition.Count;
00287 
00288             var richCharacterBatchInsertList = new List<RichCharacter> { richCharacterEnumerator.Current };
00289 
00290             while (richCharacterBatchInsertList.Count < partitionAvailableSpace && richCharacterEnumerator.MoveNext())
00291             {
00292                 richCharacterBatchInsertList.Add(richCharacterEnumerator.Current);
00293             }
00294 
00295             var inPartition = _partitionList[indexOfPartitionWithAvailableSpace];
00296             var outPartition = inPartition.InsertRange(relativePositionIndex, richCharacterBatchInsertList);
00297 
00298             _partitionList = _partitionList.SetItem(
00299                 indexOfPartitionWithAvailableSpace,
00300                 outPartition);
00301 
00302             globalPositionIndex += richCharacterBatchInsertList.Count;
00303         }
00304     }
