00306     public void __RemoveRange(int targetGlobalPositionIndex, int targetDeleteCount)
00307     {
00308         var foundTargetGlobalPositionIndex = false;
00309         // The 'searchGlobalPositionIndex' is no longer updated after 'foundTargetGlobalPositionIndex' is true
00310         // It is just being used to find where the data that should be deleted starts.
00311         var searchGlobalPositionIndex = 0;
00312         var runningDeleteCount = 0;
00313 
00314         for (int partitionIndex = 0; partitionIndex < PartitionList.Count; partitionIndex++)
00315         {
00316             var partition = PartitionList[partitionIndex];
00317             var relativePositionIndex = 0;
00318 
00319             if (!foundTargetGlobalPositionIndex)
00320             {
00321                 // It is '>' specifically, because '0 + partition.Count' is a count, therefore the
00322                 // largest index that could exist in the partition is 1 less than the partition.Count.
00323                 if (searchGlobalPositionIndex + partition.Count > targetGlobalPositionIndex)
00324                 {
00325                     foundTargetGlobalPositionIndex = true;
00326                     relativePositionIndex = targetGlobalPositionIndex - searchGlobalPositionIndex;
00327                 }
00328                 else
00329                 {
00330                     searchGlobalPositionIndex += partition.Count;
00331                     continue;
00332                 }
00333             }
00334 
00335             // This section of code is dependent on the condition branch above it having performed
00336             // a 'continue' if it was entered, but still didn't find the 'targetGlobalPositionIndex'
00337 
00338             var availableDeletes = partition.Count - relativePositionIndex;
00339             var remainingDeletes = targetDeleteCount - runningDeleteCount;
00340 
00341             var deletes = availableDeletes < remainingDeletes
00342                 ? availableDeletes
00343                 : remainingDeletes;
00344 
00345             // WARNING: The code does not currently alter the _partitionList in any way other than this 'SetItem'...
00346             //          ...invocation, with regards to this method.
00347             //          If one adds other alterations to the _partitionList in this method,
00348             //          check if this logic would break.
00349             _partitionList = _partitionList.SetItem(
00350                 partitionIndex,
00351                 partition.RemoveRange(relativePositionIndex, deletes));
00352 
00353             runningDeleteCount += deletes;
00354 
00355             if (runningDeleteCount >= targetDeleteCount)
00356                 break;
00357         }
00358     }
