00015     #region Insert_Into_EmptyEditor
00021     [Fact]
00022     public void Insert_Into_EmptyEditor_At_PositionIndex_Zero()
00023     {
00024         var (inModel, modelModifier) = EmptyEditor_TestData_And_PerformPreAssertions(
00025             resourceUri: new ResourceUri($"/{nameof(EmptyEditor_TestData_And_PerformPreAssertions)}.txt"),
00026             resourceLastWriteTime: DateTime.MinValue,
00027             fileExtension: ExtensionNoPeriodFacts.TXT,
00028             content: string.Empty,
00029             decorationMapper: null,
00030             compilerService: null,
00031             partitionSize: 4096);
00032 
00033         // Do something
00034         TextEditorModel outModel;
00035         TextEditorCursorModifier cursorModifier;
00036         {
00037             var cursor = new TextEditorCursor(lineIndex: 0, columnIndex: 0, isPrimaryCursor: true);
00038             cursorModifier = new TextEditorCursorModifier(cursor);
00039             var cursorModifierBag = new CursorModifierBagTextEditor(
00040                 Key<TextEditorViewModel>.Empty,
00041                 new List<TextEditorCursorModifier>() { cursorModifier });
00042 
00043             modelModifier.Insert(
00044                 "\n" +   // LineFeed
00045                 "b9" +   // LetterOrDigit-Lowercase
00046                 "\r" +   // CarriageReturn
00047                 "9B" +   // LetterOrDigit-Uppercase
00048                 "\r\n" + // CarriageReturnLineFeed
00049                 "\t" +   // Tab
00050                 "$" +    // SpecialCharacter
00051                 ";" +    // Punctuation
00052                 " ",     // Space
00053                 cursorModifierBag,
00054                 cancellationToken: CancellationToken.None);
00055             outModel = modelModifier.ToModel();
00056         }
00057 
00058         // Post-assertions
00059         {
00060             // The 'SetContent' parameter has a string length of '12',
00061             // But, as of this comment, the insertion of line ending characters
00062             // other than line feed is not supported.
00063             // Therefore, the "\r" and the "\r\n" are replaced with "\n".
00064             //
00065             // As a result of the "\r\n" replacement with "\n",
00066             // one character was lost, therefore the length ends up being 11.
00067             Assert.Equal(11, modelModifier.CharCount);
00068 
00069             // The file extension should NOT change as a result of setting the content.
00070             Assert.Equal(ExtensionNoPeriodFacts.TXT, modelModifier.FileExtension);
00071 
00072             // The text is small, it should write a single partition, nothing more.
00073             Assert.Single(modelModifier.PartitionList);
00074 
00075             // 1 tab key was included in the string that was inserted.
00076             // Therefore, the Count is 1.
00077             Assert.Equal(1, modelModifier.TabKeyPositionList.Count);
00078             Assert.Equal(7, modelModifier.TabKeyPositionList.Single());
00079 
00080             // LineEnd related code-block-grouping:
00081             {
00082                 // 1 CarriageReturn was inserted.
00083                 // But, for the time being, only LineFeed insertions are permitted to avoid insertion of '\r'
00084                 // into a '\n' that follows causing a "\r\n" to appear. Therefore the count is 0.
00085                 Assert.Equal(
00086                     0,
00087                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturn).count);
00088 
00089                 // 1 LineFeed was inserted.
00090                 // But, for the time being, only LineFeed insertions are permitted to avoid insertion of '\r'
00091                 // into a '\n' that follows causing a "\r\n" to appear.
00092                 // Because 3 total LineEnds were inserted, the count is 3.
00093                 Assert.Equal(
00094                     3,
00095                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.LineFeed).count);
00096                 
00097                 // 1 CarriageReturnLineFeed was inserted.
00098                 // But, for the time being, only LineFeed insertions are permitted to avoid insertion of '\r'
00099                 // into a '\n' that follows causing a "\r\n" to appear. Therefore the count is 0.
00100                 Assert.Equal(
00101                     0,
00102                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturnLineFeed).count);
00103 
00104                 var lineFeedPositionList = modelModifier.LineEndList
00105                     .Where(x => x.LineEndKind == LineEndKind.LineFeed)
00106                     .ToArray();
00107 
00108                 // First LineEnd
00109                 var lineFeedPosition = lineFeedPositionList[0];
00110                 Assert.Equal(0, lineFeedPosition.StartPositionIndexInclusive);
00111                 Assert.Equal(1, lineFeedPosition.EndPositionIndexExclusive);
00112                 // Second LineEnd
00113                 lineFeedPosition = lineFeedPositionList[1];
00114                 Assert.Equal(3, lineFeedPosition.StartPositionIndexInclusive);
00115                 Assert.Equal(4, lineFeedPosition.EndPositionIndexExclusive);
00116                 // Third LineEnd
00117                 lineFeedPosition = lineFeedPositionList[2];
00118                 Assert.Equal(6, lineFeedPosition.StartPositionIndexInclusive);
00119                 Assert.Equal(7, lineFeedPosition.EndPositionIndexExclusive);
00120 
00121                 // 3 line endings where inserted,
00122                 // There are 4 in total if one then includes the special-'EndOfFile' LineEnd.
00123                 Assert.Equal(4, modelModifier.LineEndList.Count);
00124 
00125                 // A TextEditorModel always contains at least 1 LineEnd. This LineEnd marks the 'EndOfFile'.
00126                 //
00127                 // The insert will have moved the 'EndOfFile' LineEnd from positionIndex 0 to a larger value.
00128                 // Specifically, the 'EndOfFile' should move by the length of the text inserted.
00129                 var endOfFile = modelModifier.LineEndList.Last();
00130                 Assert.Equal(LineEndKind.EndOfFile, endOfFile.LineEndKind);
00131                 Assert.Equal(11, endOfFile.StartPositionIndexInclusive);
00132                 Assert.Equal(11, endOfFile.EndPositionIndexExclusive);
00133             }
00134 
00135             // Cursor related code-block-grouping:
00136             {
00137                 var lastLine = modelModifier.GetLineInformation(modelModifier.LineCount - 1);
00138 
00139                 Assert.Equal(lastLine.Index, cursorModifier.LineIndex);
00140                 Assert.Equal(lastLine.LastValidColumnIndex, cursorModifier.ColumnIndex);
00141                 Assert.Equal(lastLine.LastValidColumnIndex, cursorModifier.PreferredColumnIndex);
00142                 Assert.True(cursorModifier.IsPrimaryCursor);
00143                 Assert.Equal(0, cursorModifier.SelectionEndingPositionIndex);
00144                 Assert.Null(cursorModifier.SelectionAnchorPositionIndex);
00145             }
00146         }
00147     }
