00400     [Fact]
00401     public void Insert_Into_EmptyEditor_Null_String()
00402     {
00403         var (inModel, modelModifier) = EmptyEditor_TestData_And_PerformPreAssertions(
00404             resourceUri: new ResourceUri($"/{nameof(EmptyEditor_TestData_And_PerformPreAssertions)}.txt"),
00405             resourceLastWriteTime: DateTime.MinValue,
00406             fileExtension: ExtensionNoPeriodFacts.TXT,
00407             content: string.Empty,
00408             decorationMapper: null,
00409             compilerService: null,
00410             partitionSize: 4096);
00411 
00412         // Post-assertions
00413         //
00414         // This test expects the 'Do Something' step to throw an exception.
00415         // That is all that needs to be tested here.
00416         Assert.Throws<NullReferenceException>(() =>
00417         {
00418             // Do something
00419             TextEditorModel outModel;
00420             {
00421                 var cursor = new TextEditorCursor(
00422                     lineIndex: 0,
00423                     columnIndex: 0,
00424                     isPrimaryCursor: true);
