00427                     Key<TextEditorViewModel>.Empty,
00428                     new List<TextEditorCursorModifier>() { cursorModifier });
00429 
00430                 modelModifier.Insert(
00431                     // The null-forgiving operator was used here
00432                     // because the test purposefully wants to pass in null,
