00453     [Fact]
00454     public void Insert_Into_EmptyEditor_Empty_String()
00455     {
00456         var (inModel, modelModifier) = EmptyEditor_TestData_And_PerformPreAssertions(
00457             resourceUri: new ResourceUri($"/{nameof(EmptyEditor_TestData_And_PerformPreAssertions)}.txt"),
00458             resourceLastWriteTime: DateTime.MinValue,
00459             fileExtension: ExtensionNoPeriodFacts.TXT,
00460             content: string.Empty,
00461             decorationMapper: null,
00462             compilerService: null,
00463             partitionSize: 4096);
00464 
00465         // Do something
00466         TextEditorModel outModel;
00467         TextEditorCursorModifier cursorModifier;
00468         {
00469             var cursor = new TextEditorCursor(
00470                 lineIndex: 0,
00471                 columnIndex: 0,
00472                 isPrimaryCursor: true);
00473             cursorModifier = new TextEditorCursorModifier(cursor);
00474             var cursorModifierBag = new CursorModifierBagTextEditor(
00475                 Key<TextEditorViewModel>.Empty,
00476                 new List<TextEditorCursorModifier>() { cursorModifier });
00477 
00478             modelModifier.Insert(
00479                 value: string.Empty,
00480                 cursorModifierBag,
00481                 cancellationToken: CancellationToken.None);
00482             outModel = modelModifier.ToModel();
00483         }
00484 
00485         // Post-assertions
00486         //
00487         // Here, the post assertions are equivalent to the Pre-Assertions because
00488         // inserting 'string.Empty' will run all of the insertion code, but have no effect.
00489         {
00490             // Obnoxiously write the constant value for the initialContent's length instead of capturing the TextEditorModel
00491             // constructor's 'initialContent' parameter, then checking '.Length'.
00492             //
00493             // This makes it more clear if the source text changes (accidentally or intentionally).
00494             // If one day this assertion fails, then someone touched the source text.
00495             Assert.Equal(0, modelModifier.CharCount);
00496 
00497             // The file extension should NOT change as a result of inserting content.
00498             Assert.Equal(ExtensionNoPeriodFacts.TXT, modelModifier.FileExtension);
00499 
00500             // The text is small, it should write a single partition, nothing more.
00501             Assert.Single(modelModifier.PartitionList);
00502 
00503             // Inserting 'string.Empty' will run all of the insertion code, but have no effect.
00504             // Therefore, the count remains 0.
00505             Assert.Equal(0, modelModifier.TabKeyPositionList.Count);
00506 
00507             // LineEnd related code-block-grouping:
00508             {
00509                 // Inserting 'string.Empty' will run all of the insertion code, but have no effect.
00510                 // Therefore, the count remains 0.
00511                 Assert.Equal(
00512                     0,
00513                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturn).count);
00514 
00515                 // Inserting 'string.Empty' will run all of the insertion code, but have no effect.
00516                 // Therefore, the count remains 0.
00517                 Assert.Equal(
00518                     0,
00519                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.LineFeed).count);
00520 
00521                 // Inserting 'string.Empty' will run all of the insertion code, but have no effect.
00522                 // Therefore, the count remains 0.
00523                 Assert.Equal(
00524                     0,
00525                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturnLineFeed).count);
00526 
00527                 // Inserting 'string.Empty' will run all of the insertion code, but have no effect.
00528                 // Therefore, the count remains 1, due to the existance of the special-'EndOfFile' line ending.
00529                 Assert.Equal(1, modelModifier.LineEndList.Count);
00530 
00531                 // Inserting 'string.Empty' will run all of the insertion code, but have no effect.
00532                 // Therefore, LineEndPositionList is expected to not contain any CarriageReturn(s)
00533                 // Assert that the only line ending in the text is the 'EndOfFile'.
00534                 Assert.Equal(LineEndKind.EndOfFile, modelModifier.LineEndList.Single().LineEndKind);
00535 
00536                 // Inserting 'string.Empty' will run all of the insertion code, but have no effect.
00537                 // Therefore, LineEndPositionList is expected to not contain any LineFeed(s)
00538                 // Assert that the only line ending in the text is the 'EndOfFile'.
00539                 Assert.Equal(LineEndKind.EndOfFile, modelModifier.LineEndList.Single().LineEndKind);
00540 
00541                 // Inserting 'string.Empty' will run all of the insertion code, but have no effect.
00542                 // Therefore, LineEndPositionList is expected to not contain any CarriageReturnLineFeed(s)
00543                 // Assert that the only line ending in the text is the 'EndOfFile'.
00544                 Assert.Equal(LineEndKind.EndOfFile, modelModifier.LineEndList.Single().LineEndKind);
00545 
00546                 // A TextEditorModel always contains at least 1 LineEnd. This LineEnd marks the 'EndOfFile'.
00547                 //
00548                 // Inserting 'string.Empty' will run all of the insertion code, but have no effect.
00549                 // Therefore, the 'EndOfFile' is unchanged.
00550                 var endOfFile = modelModifier.LineEndList.Single();
00551                 Assert.Equal(LineEndKind.EndOfFile, endOfFile.LineEndKind);
00552                 Assert.Equal(0, endOfFile.StartPositionIndexInclusive);
00553                 Assert.Equal(0, endOfFile.EndPositionIndexExclusive);
00554             }
00555 
00556             // Cursor related code-block-grouping:
00557             {
00558                 Assert.Equal(0, cursorModifier.LineIndex);
00559                 Assert.Equal(0, cursorModifier.ColumnIndex);
00560                 Assert.Equal(0, cursorModifier.PreferredColumnIndex);
00561                 Assert.True(cursorModifier.IsPrimaryCursor);
00562                 Assert.Equal(0, cursorModifier.SelectionEndingPositionIndex);
00563                 Assert.Null(cursorModifier.SelectionAnchorPositionIndex);
00564             }
00565         }
00566     }
