00575     [Fact]
00576     public void Insert_Into_NotEmptyEditor_At_PositionIndex_Zero()
00577     {
00578         var (inModel, modelModifier) = NotEmptyEditor_TestData_And_PerformPreAssertions(
00579             resourceUri: new ResourceUri($"/{nameof(NotEmptyEditor_TestData_And_PerformPreAssertions)}.txt"),
00580             resourceLastWriteTime: DateTime.MinValue,
00581             fileExtension: ExtensionNoPeriodFacts.TXT,
00582             content:
00583                 (
00584                     "\n" +   // LineFeed
00585                     "b9" +   // LetterOrDigit-Lowercase
00586                     "\r" +   // CarriageReturn
00587                     "9B" +   // LetterOrDigit-Uppercase
00588                     "\r\n" + // CarriageReturnLineFeed
00589                     "\t" +   // Tab
00590                     "$" +    // SpecialCharacter
00591                     ";" +    // Punctuation
00592                     " "      // Space
00593                 ),
00594             decorationMapper: null,
00595             compilerService: null,
00596             partitionSize: 4096);
00597 
00598         // Do something
00599         TextEditorModel outModel;
00600         TextEditorCursorModifier cursorModifier;
00601         {
00602             var cursor = new TextEditorCursor(
00603                 lineIndex: 0,
00604                 columnIndex: 0,
00605                 isPrimaryCursor: true);
00606             cursorModifier = new TextEditorCursorModifier(cursor);
00607             var cursorModifierBag = new CursorModifierBagTextEditor(
00608                 Key<TextEditorViewModel>.Empty,
00609                 new List<TextEditorCursorModifier>() { cursorModifier });
00610 
00611             modelModifier.Insert(
00612                 "\n" +   // LineFeed
00613                 "b9" +   // LetterOrDigit-Lowercase
00614                 "\r" +   // CarriageReturn
00615                 "9B" +   // LetterOrDigit-Uppercase
00616                 "\r\n" + // CarriageReturnLineFeed
00617                 "\t" +   // Tab
00618                 "$" +    // SpecialCharacter
00619                 ";" +    // Punctuation
00620                 " ",     // Space
00621                 cursorModifierBag,
00622                 cancellationToken: CancellationToken.None);
00623             outModel = modelModifier.ToModel();
00624         }
00625 
00626         // Post-assertions
00627         {
00628             // The 'Insert(...)' parameter has a string length of '12',
00629             // But, as of this comment, the insertion of line ending characters
00630             // other than line feed is not supported.
00631             // Therefore, the "\r" and the "\r\n" are replaced with "\n".
00632             //
00633             // As a result of the "\r\n" replacement with "\n",
00634             // one character was lost, therefore the length ends up being 11.
00635             //
00636             // The inital content's length was 12,
00637             // so the length after insertion is 23
00638             Assert.Equal(23, modelModifier.CharCount);
00639 
00640             // The file extension should NOT change as a result of inserting content.
00641             Assert.Equal(ExtensionNoPeriodFacts.TXT, modelModifier.FileExtension);
00642 
00643             // The text is small, it should write a single partition, nothing more.
00644             Assert.Single(modelModifier.PartitionList);
00645 
00646             // 1 tab key was included in the initial content for the TextEditorModel.
00647             // Furthermore, 1 tab key was inserted.
00648             // Therefore the count is 2.
00649             Assert.Equal(2, modelModifier.TabKeyPositionList.Count);
00650             // First tab key
00651             var tabKeyPosition = modelModifier.TabKeyPositionList[0];
00652             Assert.Equal(7, tabKeyPosition);
00653             // Second tab key
00654             tabKeyPosition = modelModifier.TabKeyPositionList[1];
00655             Assert.Equal(19, tabKeyPosition);
00656 
00657             // LineEnd related code-block-grouping:
00658             {
00659                 // 1 CarriageReturn was included in the initial content for the TextEditorModel.
00660                 // But, for the time being, only LineFeed insertions are permitted to avoid insertion of '\r'
00661                 // into a '\n' that follows causing a "\r\n" to appear. Therefore the count remains 1.
00662                 Assert.Equal(
00663                     1,
00664                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturn).count);
00665                 var carriageReturn = modelModifier.LineEndList.Single(x => x.LineEndKind == LineEndKind.CarriageReturn);
00666                 Assert.Equal(14, carriageReturn.StartPositionIndexInclusive);
00667                 Assert.Equal(15, carriageReturn.EndPositionIndexExclusive);
00668 
00669                 // 1 LineFeed was included in the initial content for the TextEditorModel.
00670                 // But, for the time being, only LineFeed insertions are permitted to avoid insertion of '\r'
00671                 // into a '\n' that follows causing a "\r\n" to appear.
00672                 // Because 3 total LineEnds were inserted, the count of LineFeed(s) is 4.
00673                 Assert.Equal(
00674                     4,
00675                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.LineFeed).count);
00676                 var lineFeedMatches = modelModifier.LineEndList.Where(x => x.LineEndKind == LineEndKind.LineFeed).ToArray();
00677                 // First LineFeed
00678                 var lineFeed = lineFeedMatches[0];
00679                 Assert.Equal(0, lineFeed.StartPositionIndexInclusive);
00680                 Assert.Equal(1, lineFeed.EndPositionIndexExclusive);
00681                 // Second LineFeed
00682                 lineFeed = lineFeedMatches[1];
00683                 Assert.Equal(3, lineFeed.StartPositionIndexInclusive);
00684                 Assert.Equal(4, lineFeed.EndPositionIndexExclusive);
00685                 // Third LineFeed
00686                 lineFeed = lineFeedMatches[2];
00687                 Assert.Equal(6, lineFeed.StartPositionIndexInclusive);
00688                 Assert.Equal(7, lineFeed.EndPositionIndexExclusive);
00689                 // Fourth LineFeed
00690                 lineFeed = lineFeedMatches[3];
00691                 Assert.Equal(11, lineFeed.StartPositionIndexInclusive);
00692                 Assert.Equal(12, lineFeed.EndPositionIndexExclusive);
00693 
00694                 // 1 CarriageReturnLineFeed was included in the initial content for the TextEditorModel.
00695                 // But, for the time being, only LineFeed insertions are permitted to avoid insertion of '\r'
00696                 // into a '\n' that follows causing a "\r\n" to appear. Therefore the count remains 1.
00697                 Assert.Equal(
00698                     1,
00699                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturnLineFeed).count);
00700                 var carriageReturnLineFeed = modelModifier.LineEndList.Single(x => x.LineEndKind == LineEndKind.CarriageReturnLineFeed);
00701                 Assert.Equal(17, carriageReturnLineFeed.StartPositionIndexInclusive);
00702                 Assert.Equal(19, carriageReturnLineFeed.EndPositionIndexExclusive);
00703 
00704                 // 3 line endings were part of the constructor's initialContent,
00705                 // 3 line endings where inserted,
00706                 // Therefore, there are 7 in total if one then includes the special-'EndOfFile' LineEnd.
00707                 Assert.Equal(7, modelModifier.LineEndList.Count);
00708 
00709                 // A TextEditorModel always contains at least 1 LineEnd. This LineEnd marks the 'EndOfFile'.
00710                 //
00711                 // The insert will have moved the 'EndOfFile' LineEnd from positionIndex 12 to a larger value.
00712                 // Specifically, the 'EndOfFile' should move by the length of the text inserted.
00713                 var endOfFile = modelModifier.LineEndList.Last();
00714                 Assert.Equal(LineEndKind.EndOfFile, endOfFile.LineEndKind);
00715                 Assert.Equal(23, endOfFile.StartPositionIndexInclusive);
00716                 Assert.Equal(23, endOfFile.EndPositionIndexExclusive);
00717             }
00718 
00719             // Cursor related code-block-grouping:
00720             {
00721                 // Insertion was done at (rowIndex: 0, columnIndex: 0).
00722                 //
00723                 // The text inserted has its endpoint at (rowIndex: 3, columnIndex: 4)
00724                 //
00725                 // Therefore, end result is cursor_rowIndex + insertionContentFinalRowIndex,
00726                 // and the columnIndex ends up being the insertionContentFinalColumnIndex.
00727                 //
00728                 // Which is (rowIndex: 3, columnIndex: 4)
00729                 Assert.Equal(3, cursorModifier.LineIndex);
00730                 Assert.Equal(4, cursorModifier.ColumnIndex);
00731                 Assert.Equal(4, cursorModifier.PreferredColumnIndex);
00732                 Assert.True(cursorModifier.IsPrimaryCursor);
00733                 Assert.Equal(0, cursorModifier.SelectionEndingPositionIndex);
00734                 Assert.Null(cursorModifier.SelectionAnchorPositionIndex);
00735             }
00736         }
00737     }
