00916     [Fact]
00917     public void Insert_Into_NotEmptyEditor_At_PositionIndex_Between_0_And_DocumentLength_Exclusive()
00918     {
00919         var (inModel, modelModifier) = NotEmptyEditor_TestData_And_PerformPreAssertions(
00920             resourceUri: new ResourceUri($"/{nameof(NotEmptyEditor_TestData_And_PerformPreAssertions)}.txt"),
00921             resourceLastWriteTime: DateTime.MinValue,
00922             fileExtension: ExtensionNoPeriodFacts.TXT,
00923             content:
00924                 (
00925                     "\n" +   // LineFeed
00926                     "b9" +   // LetterOrDigit-Lowercase
00927                     "\r" +   // CarriageReturn
00928                     "9B" +   // LetterOrDigit-Uppercase
00929                     "\r\n" + // CarriageReturnLineFeed
00930                     "\t" +   // Tab
00931                     "$" +    // SpecialCharacter
00932                     ";" +    // Punctuation
00933                     " "      // Space
00934                 ),
00935             decorationMapper: null,
00936             compilerService: null,
00937             partitionSize: 4096);
00938 
00939         // Do something
00940         TextEditorModel outModel;
00941         TextEditorCursorModifier cursorModifier;
00942         {
00943             var cursor = new TextEditorCursor(
00944                 lineIndex: 1,
00945                 columnIndex: 1,
00946                 isPrimaryCursor: true);
00947             cursorModifier = new TextEditorCursorModifier(cursor);
00948             var cursorModifierBag = new CursorModifierBagTextEditor(
00949                 Key<TextEditorViewModel>.Empty,
00950                 new List<TextEditorCursorModifier>() { cursorModifier });
00951 
00952             modelModifier.SetLineEndKindPreference(LineEndKind.LineFeed);
00953 
00954             modelModifier.Insert(
00955                     "\n" +   // LineFeed
00956                     "b9" +   // LetterOrDigit-Lowercase
00957                     "\r" +   // CarriageReturn
00958                     "9B" +   // LetterOrDigit-Uppercase
00959                     "\r\n" + // CarriageReturnLineFeed
00960                     "\t" +   // Tab
00961                     "$" +    // SpecialCharacter
00962                     ";" +    // Punctuation
00963                     " ",     // Space
00964                     cursorModifierBag,
00965                     cancellationToken: CancellationToken.None);
00966             outModel = modelModifier.ToModel();
00967         }
00968 
00969         // Post-assertions
00970         {
00971             Assert.Equal(23, modelModifier.CharCount);
00972             Assert.Equal(ExtensionNoPeriodFacts.TXT, modelModifier.FileExtension);
00973             Assert.Single(modelModifier.PartitionList);
00974             Assert.Equal(2, modelModifier.TabKeyPositionList.Count);
00975 
00976             // LineEnd related code-block-grouping:
00977             {
00978                 // The constructor invocation inserted 'as is', prior to the insertion step.
00979                 Assert.Equal(
00980                     1,
00981                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturn).count);
00982                 var carriageReturn = modelModifier.LineEndList.Single(x => x.LineEndKind == LineEndKind.CarriageReturn);
00983                 Assert.Equal(14, carriageReturn.StartPositionIndexInclusive);
00984                 Assert.Equal(15, carriageReturn.EndPositionIndexExclusive);
00985 
00986                 Assert.Equal(
00987                     4,
00988                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.LineFeed).count);
00989                 var lineFeedMatches = modelModifier.LineEndList.Where(x => x.LineEndKind == LineEndKind.LineFeed).ToArray();
00990                 // First LineFeed
00991                 var lineFeed = lineFeedMatches[0];
00992                 Assert.Equal(0, lineFeed.StartPositionIndexInclusive);
00993                 Assert.Equal(1, lineFeed.EndPositionIndexExclusive);
00994                 // Second LineFeed
00995                 lineFeed = lineFeedMatches[1];
00996                 Assert.Equal(2, lineFeed.StartPositionIndexInclusive);
00997                 Assert.Equal(3, lineFeed.EndPositionIndexExclusive);
00998                 // Third LineFeed
00999                 lineFeed = lineFeedMatches[2];
01000                 Assert.Equal(5, lineFeed.StartPositionIndexInclusive);
01001                 Assert.Equal(6, lineFeed.EndPositionIndexExclusive);
01002                 // Fourth LineFeed
01003                 lineFeed = lineFeedMatches[3];
01004                 Assert.Equal(8, lineFeed.StartPositionIndexInclusive);
01005                 Assert.Equal(9, lineFeed.EndPositionIndexExclusive);
01006 
01007                 Assert.Equal(
01008                     1,
01009                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturnLineFeed).count);
01010                 var carriageReturnLineFeed = modelModifier.LineEndList.Single(x => x.LineEndKind == LineEndKind.CarriageReturnLineFeed);
01011                 // StartPositionIndexInclusive
01012                 Assert.Equal(17, carriageReturnLineFeed.StartPositionIndexInclusive);
01013                 // EndPositionIndexExclusive
01014                 Assert.Equal(19, carriageReturnLineFeed.EndPositionIndexExclusive);
01015                 
01016                 Assert.Equal(7, modelModifier.LineEndList.Count);
01017 
01018                 var endOfFile = modelModifier.LineEndList.Last();
01019                 Assert.Equal(LineEndKind.EndOfFile, endOfFile.LineEndKind);
01020                 Assert.Equal(23, endOfFile.StartPositionIndexInclusive);
01021                 Assert.Equal(23, endOfFile.EndPositionIndexExclusive);
01022             }
01023 
01024             // Cursor related code-block-grouping:
01025             {
01026                 Assert.Equal(4, cursorModifier.LineIndex);
01027                 Assert.Equal(4, cursorModifier.ColumnIndex);
01028                 Assert.Equal(4, cursorModifier.PreferredColumnIndex);
01029                 Assert.True(cursorModifier.IsPrimaryCursor);
01030                 Assert.Equal(0, cursorModifier.SelectionEndingPositionIndex);
01031                 Assert.Null(cursorModifier.SelectionAnchorPositionIndex);
01032             }
01033         }
