01181         var (inModel, modelModifier) = NotEmptyEditor_TestData_And_PerformPreAssertions(
01182             resourceUri: new ResourceUri($"/{nameof(NotEmptyEditor_TestData_And_PerformPreAssertions)}.txt"),
01183             resourceLastWriteTime: DateTime.MinValue,
01184             fileExtension: ExtensionNoPeriodFacts.TXT,
01185             content:
01186                 (
01187                     "\n" +   // LineFeed
01188                     "b9" +   // LetterOrDigit-Lowercase
01189                     "\r" +   // CarriageReturn
01190                     "9B" +   // LetterOrDigit-Uppercase
01191                     "\r\n" + // CarriageReturnLineFeed
01192                     "\t" +   // Tab
01193                     "$" +    // SpecialCharacter
01194                     ";" +    // Punctuation
01195                     " "      // Space
01196                 ),
01197             decorationMapper: null,
01198             compilerService: null,
01199             partitionSize: 4096);
01200 
01201         // Post-assertions
01202         //
01203         // This test expects the 'Do Something' step to throw an exception.
01204         // That is all that needs to be tested here.
01205         Assert.Throws<NullReferenceException>(() =>
01206         {
01207             // Do something
01208             TextEditorModel outModel;
01209             {
01210                 var cursor = new TextEditorCursor(
01211                         lineIndex: 0,
01212                         columnIndex: 0,
01213                         isPrimaryCursor: true);
01214                 var cursorModifier = new TextEditorCursorModifier(cursor);
01215                 var cursorModifierBag = new CursorModifierBagTextEditor(
01216                     Key<TextEditorViewModel>.Empty,
01217                     new List<TextEditorCursorModifier>() { cursorModifier });
01218 
01219                 modelModifier.Insert(
01220                     // The null-forgiving operator was used here
01221                     // because the test purposefully wants to pass in null,
01222                     // to see what happens.
01223                     value: null!,
01224                     cursorModifierBag,
01225                     cancellationToken: CancellationToken.None);
01226                 outModel = modelModifier.ToModel();
01227             }
01228         });
01229     }
