01242     [Fact]
01243     public void Insert_Into_NotEmptyEditor_Empty_String()
01244     {
01245         var (inModel, modelModifier) = NotEmptyEditor_TestData_And_PerformPreAssertions(
01246             resourceUri: new ResourceUri($"/{nameof(NotEmptyEditor_TestData_And_PerformPreAssertions)}.txt"),
01247             resourceLastWriteTime: DateTime.MinValue,
01248             fileExtension: ExtensionNoPeriodFacts.TXT,
01249             content:
01250                 (
01251                     "\n" +   // LineFeed
01252                     "b9" +   // LetterOrDigit-Lowercase
01253                     "\r" +   // CarriageReturn
01254                     "9B" +   // LetterOrDigit-Uppercase
01255                     "\r\n" + // CarriageReturnLineFeed
01256                     "\t" +   // Tab
01257                     "$" +    // SpecialCharacter
01258                     ";" +    // Punctuation
01259                     " "      // Space
01260                 ),
01261             decorationMapper: null,
01262             compilerService: null,
01263             partitionSize: 4096);
01264 
01265         // Do something
01266         TextEditorModel outModel;
01267         TextEditorCursorModifier cursorModifier;
01268         {
01269             var cursor = new TextEditorCursor(
01270                 lineIndex: 0,
01271                 columnIndex: 0,
01272                 isPrimaryCursor: true);
01273             cursorModifier = new TextEditorCursorModifier(cursor);
01274             var cursorModifierBag = new CursorModifierBagTextEditor(
01275                 Key<TextEditorViewModel>.Empty,
01276                 new List<TextEditorCursorModifier>() { cursorModifier });
01277 
01278             modelModifier.Insert(
01279                 value: string.Empty,
01280                 cursorModifierBag,
01281                 cancellationToken: CancellationToken.None);
01282             outModel = modelModifier.ToModel();
01283         }
01284 
01285         // Post-assertions
01286         //
01287         // Here, the post assertions are equivalent to the Pre-Assertions because
01288         // inserting 'string.Empty' will run all of the insertion code, but have no effect.
01289         {
01290             // Obnoxiously write the constant value for the initialContent's length instead of capturing the TextEditorModel
01291             // constructor's 'initialContent' parameter, then checking '.Length'.
01292             //
01293             // This makes it more clear if the source text changes (accidentally or intentionally).
01294             // If one day this assertion fails, then someone touched the source text.
01295             Assert.Equal(12, modelModifier.CharCount);
01296 
01297             // The file extension should NOT change as a result of inserting content.
01298             Assert.Equal(ExtensionNoPeriodFacts.TXT, modelModifier.FileExtension);
01299 
01300             // The text is small, it should write a single partition, nothing more.
01301             Assert.Single(modelModifier.PartitionList);
01302 
01303             // 1 tab key was included in the string that was passed to the constructor.
01304             // And the insertion is expected to do nothing.
01305             // Therefore, the Count remains 1.
01306             Assert.Equal(1, modelModifier.TabKeyPositionList.Count);
01307             Assert.Equal(8, modelModifier.TabKeyPositionList.Single());
01308 
01309             // LineEnd related code-block-grouping:
01310             {
01311                 // 1 CarriageReturn was included in the string that was passed to the constructor.
01312                 // And the insertion is expected to do nothing.
01313                 // Therefore, the Count remains 1.
01314                 // NOTE: While the Insert(...) method does not allow '\r' or '\r\n', the constructor does.
01315                 Assert.Equal(
01316                     1,
01317                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturn).count);
01318                 var carriageReturn = modelModifier.LineEndList.Single(x => x.LineEndKind == LineEndKind.CarriageReturn);
01319                 Assert.Equal(3, carriageReturn.StartPositionIndexInclusive);
01320                 Assert.Equal(4, carriageReturn.EndPositionIndexExclusive);
01321 
01322                 // 1 LineFeed was included in the string that was passed to the constructor.
01323                 // And the insertion is expected to do nothing.
01324                 // Therefore, the Count remains 1.
01325                 Assert.Equal(
01326                     1,
01327                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.LineFeed).count);
01328                 var lineFeed = modelModifier.LineEndList.Single(x => x.LineEndKind == LineEndKind.LineFeed);
01329                 Assert.Equal(0, lineFeed.StartPositionIndexInclusive);
01330                 Assert.Equal(1, lineFeed.EndPositionIndexExclusive);
01331 
01332                 // 1 CarriageReturnLineFeed was included in the string that was passed to the constructor.
01333                 // And the insertion is expected to do nothing.
01334                 // Therefore, the Count remains 1.
01335                 // NOTE: While the Insert(...) method does not allow '\r' or '\r\n', the constructor does.
01336                 Assert.Equal(
01337                     1,
01338                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturnLineFeed).count);
01339                 var carriageReturnLineFeed = modelModifier.LineEndList.Single(x => x.LineEndKind == LineEndKind.CarriageReturnLineFeed);
01340                 Assert.Equal(6, carriageReturnLineFeed.StartPositionIndexInclusive);
01341                 Assert.Equal(8, carriageReturnLineFeed.EndPositionIndexExclusive);
01342 
01343                 // 3 line endings where included in the string that was passed to the constructor,
01344                 // And the insertion is expected to do nothing.
01345                 // Therefore, there are 4 in total if one then includes the special-'EndOfFile' LineEnd.
01346                 Assert.Equal(4, modelModifier.LineEndList.Count);
01347 
01348                 // A TextEditorModel always contains at least 1 LineEnd. This LineEnd marks the 'EndOfFile'.
01349                 //
01350                 // The insertion is expected to do nothing,
01351                 // therefore the positionIndex remains 12.
01352                 var endOfFile = modelModifier.LineEndList.Last();
01353                 Assert.Equal(LineEndKind.EndOfFile, endOfFile.LineEndKind);
01354                 Assert.Equal(12, endOfFile.StartPositionIndexInclusive);
01355                 Assert.Equal(12, endOfFile.EndPositionIndexExclusive);
01356             }
01357 
01358             // Cursor related code-block-grouping:
01359             {
01360                 Assert.Equal(0, cursorModifier.LineIndex);
01361                 Assert.Equal(0, cursorModifier.ColumnIndex);
01362                 Assert.Equal(0, cursorModifier.PreferredColumnIndex);
01363                 Assert.True(cursorModifier.IsPrimaryCursor);
01364                 Assert.Equal(0, cursorModifier.SelectionEndingPositionIndex);
01365                 Assert.Null(cursorModifier.SelectionAnchorPositionIndex);
01366             }
01367         }
01368     }
01369 
01377     [Fact]
01378     public void Insert_Into_NotEmptyEditor_At_PositionIndex_Between_0_And_DocumentLength_Exclusive_With_Selection()
01379     {
01380         var (inModel, modelModifier) = NotEmptyEditor_TestData_And_PerformPreAssertions(
01381             resourceUri: new ResourceUri($"/{nameof(NotEmptyEditor_TestData_And_PerformPreAssertions)}.txt"),
01382             resourceLastWriteTime: DateTime.MinValue,
01383             fileExtension: ExtensionNoPeriodFacts.TXT,
01384             content:
01385                 (
01386                     "\n" +   // LineFeed
01387                     "b9" +   // LetterOrDigit-Lowercase
01388                     "\r" +   // CarriageReturn
01389                     "9B" +   // LetterOrDigit-Uppercase
01390                     "\r\n" + // CarriageReturnLineFeed
01391                     "\t" +   // Tab
01392                     "$" +    // SpecialCharacter
01393                     ";" +    // Punctuation
01394                     " "      // Space
01395                 ),
01396             decorationMapper: null,
01397             compilerService: null,
01398             partitionSize: 4096);
01399 
01400         // Do something
01401         TextEditorModel outModel;
01402         TextEditorCursorModifier cursorModifier;
01403         {
01404             // Select "B\r\n"
01405             var cursor = new TextEditorCursor(
01406                 LineIndex: 1,
01407                 ColumnIndex: 1,
01408                 PreferredColumnIndex: 1,
01409                 IsPrimaryCursor: true,
01410                 new TextEditorSelection(
01411                     5,
01412                     8));
01413             cursorModifier = new TextEditorCursorModifier(cursor);
01414             var cursorModifierBag = new CursorModifierBagTextEditor(
01415                 Key<TextEditorViewModel>.Empty,
01416                 new List<TextEditorCursorModifier>() { cursorModifier });
01417 
01418             modelModifier.SetLineEndKindPreference(LineEndKind.LineFeed);
01419             modelModifier.Insert(
01420                 "\n" +   // LineFeed
01421                 "b9" +   // LetterOrDigit-Lowercase
01422                 "\r" +   // CarriageReturn
01423                 "9B" +   // LetterOrDigit-Uppercase
01424                 "\r\n" + // CarriageReturnLineFeed
01425                 "\t" +   // Tab
01426                 "$" +    // SpecialCharacter
01427                 ";" +    // Punctuation
01428                 " ",     // Space
01429                 cursorModifierBag,
01430                 cancellationToken: CancellationToken.None);
01431             outModel = modelModifier.ToModel();
01432         }
01433 
01434         // Post-assertions
01435         {
01436             Assert.Equal(20, modelModifier.CharCount);
01437             Assert.Equal(ExtensionNoPeriodFacts.TXT, modelModifier.FileExtension);
01438             Assert.Single(modelModifier.PartitionList);
01439             Assert.Equal(2, modelModifier.TabKeyPositionList.Count);
01440 
01441             // LineEnd related code-block-grouping:
01442             {
01443                 Assert.Equal(
01444                     1,
01445                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturn).count);
01446                 var carriageReturn = modelModifier.LineEndList.Single(x => x.LineEndKind == LineEndKind.CarriageReturn);
01447                 Assert.Equal(3, carriageReturn.StartPositionIndexInclusive);
01448                 Assert.Equal(4, carriageReturn.EndPositionIndexExclusive);
01449 
01450                 Assert.Equal(4, modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.LineFeed).count);
01451                 var lineFeedMatches = modelModifier.LineEndList.Where(x => x.LineEndKind == LineEndKind.LineFeed).ToArray();
01452                 // First LineFeed
01453                 var lineFeed = lineFeedMatches[0];
01454                 Assert.Equal(0, lineFeed.StartPositionIndexInclusive);
01455                 Assert.Equal(1, lineFeed.EndPositionIndexExclusive);
01456                 // Second LineFeed
01457                 lineFeed = lineFeedMatches[1];
01458                 Assert.Equal(5, lineFeed.StartPositionIndexInclusive);
01459                 Assert.Equal(6, lineFeed.EndPositionIndexExclusive);
01460                 // Third LineFeed
01461                 lineFeed = lineFeedMatches[2];
01462                 Assert.Equal(8, lineFeed.StartPositionIndexInclusive);
01463                 Assert.Equal(9, lineFeed.EndPositionIndexExclusive);
01464                 // Fourth LineFeed
01465                 lineFeed = lineFeedMatches[3];
01466                 Assert.Equal(11, lineFeed.StartPositionIndexInclusive);
01467                 Assert.Equal(12, lineFeed.EndPositionIndexExclusive);
01468 
01469                 Assert.Equal(
01470                     0,
01471                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturnLineFeed).count);
01472 
01473                 Assert.Equal(6, modelModifier.LineEndList.Count);
01474 
01475                 var endOfFile = modelModifier.LineEndList.Last();
01476                 Assert.Equal(LineEndKind.EndOfFile, endOfFile.LineEndKind);
01477                 Assert.Equal(20, endOfFile.StartPositionIndexInclusive);
01478                 Assert.Equal(20, endOfFile.EndPositionIndexExclusive);
01479             }
01480 
01481             // Cursor related code-block-grouping:
01482             {
01483                 Assert.Equal(5, cursorModifier.LineIndex);
01484                 Assert.Equal(4, cursorModifier.ColumnIndex);
01485                 Assert.Equal(4, cursorModifier.PreferredColumnIndex);
01486                 Assert.True(cursorModifier.IsPrimaryCursor);
01487                 Assert.Equal(0, cursorModifier.SelectionEndingPositionIndex);
01488                 Assert.Null(cursorModifier.SelectionAnchorPositionIndex);
01489             }
01490         }
01491     }
