00094     [Fact]
00095     public void Enter_Enter_Backspace_LineFeed()
00096     {
00097         var (inModel, modelModifier) = EmptyEditor_TestData_And_PerformPreAssertions(
00098             resourceUri: new ResourceUri($"/{nameof(EmptyEditor_TestData_And_PerformPreAssertions)}.txt"),
00099             resourceLastWriteTime: DateTime.MinValue,
00100             fileExtension: ExtensionNoPeriodFacts.TXT,
00101             content: string.Empty,
00102             decorationMapper: null,
00103             compilerService: null,
00104             partitionSize: 4096);
00105 
00106         var cursor = new TextEditorCursor(
00107             lineIndex: 0,
00108             columnIndex: 0,
00109             isPrimaryCursor: true);
00110 
00111         var cursorModifier = new TextEditorCursorModifier(cursor);
00112         var cursorModifierBag = new CursorModifierBagTextEditor(
00113             Key<TextEditorViewModel>.Empty,
00114             new List<TextEditorCursorModifier>() { cursorModifier });
00115 
00116         // Insert 2 LineFeed characters.
00117         var insertionString = "\n\n";
00118         modelModifier.Insert(insertionString, cursorModifierBag, cancellationToken: CancellationToken.None);
00119 
00120         // Assert insertion
00121         {
00122             Assert.Equal(insertionString, modelModifier.GetAllText());
00123             Assert.Equal(3, modelModifier.LineEndList.Count);
00124 
00125             // First line end
00126             {
00127                 var lineEnd = modelModifier.LineEndList[0];
00128                 Assert.Equal(0, lineEnd.StartPositionIndexInclusive);
00129                 Assert.Equal(1, lineEnd.EndPositionIndexExclusive);
00130                 Assert.Equal(LineEndKind.LineFeed, lineEnd.LineEndKind);
00131             }
00132 
00133             // Second line end
00134             {
00135                 var lineEnd = modelModifier.LineEndList[1];
00136                 Assert.Equal(1, lineEnd.StartPositionIndexInclusive);
00137                 Assert.Equal(2, lineEnd.EndPositionIndexExclusive);
00138                 Assert.Equal(LineEndKind.LineFeed, lineEnd.LineEndKind);
00139             }
00140 
00141             // Third line end
00142             {
00143                 var lineEnd = modelModifier.LineEndList[2];
00144                 Assert.Equal(2, lineEnd.StartPositionIndexInclusive);
00145                 Assert.Equal(2, lineEnd.EndPositionIndexExclusive);
00146                 Assert.Equal(LineEndKind.EndOfFile, lineEnd.LineEndKind);
00147             }
00148         }
00149 
00150         // Backspace with count of 1
00151         modelModifier.Delete(cursorModifierBag, 1, false, TextEditorModelModifier.DeleteKind.Backspace, CancellationToken.None);
00152 
00153         // Assert backspace
00154         {
00155             Assert.Equal("\n", modelModifier.GetAllText());
00156             Assert.Equal(2, modelModifier.LineEndList.Count);
00157 
00158             var firstLineEnd = modelModifier.LineEndList[0];
00159             Assert.Equal(0, firstLineEnd.StartPositionIndexInclusive);
00160             Assert.Equal(1, firstLineEnd.EndPositionIndexExclusive);
00161 
00162             var lastLineEnd = modelModifier.LineEndList[1];
00163             Assert.Equal(1, lastLineEnd.StartPositionIndexInclusive);
00164             Assert.Equal(1, lastLineEnd.EndPositionIndexExclusive);
00165         }
00166     }
