00327     [Fact]
00328     public void ClearContent()
00329     {
00330         // Create test data
00331         TextEditorModel inModel;
00332         TextEditorModelModifier modelModifier;
00333         {
00334             inModel = new TextEditorModel(
00335                 new ResourceUri($"/{nameof(ClearContent)}.txt"),
00336                 DateTime.UtcNow,
00337                 ExtensionNoPeriodFacts.TXT,
00338                 (
00339                     "A2" + // Uppercase letter, and Digit
00340                     "\n" + // LineFeed
00341                     "\r" + // CarriageReturn
00342                     "z$" + // Lowercase letter, and special character
00343                     "\t" + // Tab
00344                     "\r\n" // CarriageReturnLineFeed
00345                 ),
00346                 null,
00347                 null,
00348                 // Partition size is defaulted to 4096, but explicitly passing the value ensures that
00349                 // a change in the default value won't break this test.
00350                 partitionSize: 4096); 
00351 
00352             modelModifier = new TextEditorModelModifier(inModel);
00353         }
00354 
00355         // Pre-assertions
00356         {
00357             // Obnoxiously write the constant '9' instead of capturing the TextEditorModel
00358             // constructor's 'initialContent' parameter, then checking '.Length'.
00359             //
00360             // This makes it more clear if the source text changes (accidentally or intentionally).
00361             // If one day this assertion fails, then someone touched the source text.
00362             Assert.Equal(9, modelModifier.CharCount);
00363 
00364             // The file extension should NOT change as a result of clearing the content.
00365             Assert.Equal(ExtensionNoPeriodFacts.TXT, modelModifier.FileExtension);
00366 
00367             // The text is small, it should write a single partition, nothing more.
00368             Assert.Single(modelModifier.PartitionList);
00369 
00370             // 1 tab key was included in the initial content for the TextEditorModel, therefore the Count is 1.
00371             Assert.Equal(1, modelModifier.TabKeyPositionList.Count);
00372 
00373             // LineEnd related code-block-grouping:
00374             {
00375                 // 1 CarriageReturn was included in the initial content for the TextEditorModel, therefore the count is 1.
00376                 Assert.Equal(
00377                     1,
00378                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturn).count);
00379 
00380                 // 1 LineFeed was included in the initial content for the TextEditorModel, therefore the count is 1.
00381                 Assert.Equal(
00382                     1,
00383                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.LineFeed).count);
00384 
00385                 // 1 CarriageReturnLineFeed was included in the initial content for the TextEditorModel, therefore the count is 1.
00386                 Assert.Equal(
00387                     1,
00388                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturnLineFeed).count);
00389 
00390                 // 3 line endings where included in the initial content for the TextEditorModel,
00391                 // plus the always existing 'EndOfFile' line ending, means the Count is 4.
00392                 Assert.Equal(4, modelModifier.LineEndList.Count);
00393 
00394                 // When invoking the constructor for the TextEditorModel, a LineFeed was
00395                 // the first LineEnd occurence, in regards to position from start to end.
00396                 var lineFeed = modelModifier.LineEndList[0];
00397                 Assert.Equal(2, lineFeed.StartPositionIndexInclusive);
00398                 Assert.Equal(3, lineFeed.EndPositionIndexExclusive);
00399                 Assert.Equal(LineEndKind.LineFeed, lineFeed.LineEndKind);
00400 
00401                 // When invoking the constructor for the TextEditorModel, a CarriageReturn was
00402                 // the second LineEnd occurence, in regards to position from start to end.
00403                 var carriageReturn = modelModifier.LineEndList[1];
00404                 Assert.Equal(3, carriageReturn.StartPositionIndexInclusive);
00405                 Assert.Equal(4, carriageReturn.EndPositionIndexExclusive);
00406                 Assert.Equal(LineEndKind.CarriageReturn, carriageReturn.LineEndKind);
00407 
00408                 // When invoking the constructor for the TextEditorModel, a CarriageReturnLineFeed was
00409                 // the third LineEnd occurence, in regards to position from start to end.
00410                 var carriageReturnLineFeed = modelModifier.LineEndList[2];
00411                 Assert.Equal(7, carriageReturnLineFeed.StartPositionIndexInclusive);
00412                 Assert.Equal(9, carriageReturnLineFeed.EndPositionIndexExclusive);
00413                 Assert.Equal(LineEndKind.CarriageReturnLineFeed, carriageReturnLineFeed.LineEndKind);
00414 
00415                 // A TextEditorModel always contains at least 1 LineEnd.
00416                 // This LineEnd marks the 'EndOfFile'.
00417                 //
00418                 // Given that the constructor for 'TextEditorModel' takes the 'initialContent'
00419                 // and sets the model's content as it, the 'EndOfFile' is no longer at positionIndex 0,
00420                 // but instead shifted by the length of the 'initialContent'.
00421                 var endOfFile = modelModifier.LineEndList[3];
00422                 Assert.Equal(9, endOfFile.StartPositionIndexInclusive);
00423                 Assert.Equal(9, endOfFile.EndPositionIndexExclusive);
00424                 Assert.Equal(LineEndKind.EndOfFile, endOfFile.LineEndKind);
00425             }
00426         }
00427 
00428         // Do something
00429         TextEditorModel outModel;
00430         {
00431             modelModifier.ClearContent();
00432             outModel = modelModifier.ToModel();
00433         }
00434 
00435         // Post-assertions
00436         {
00437             // Clearing the content of a 'TextEditorModel' should result in a 'DocumentLength' of 0.
00438             Assert.Equal(0, modelModifier.CharCount);
00439 
00440             // The file extension should NOT change as a result of clearing the content.
00441             Assert.Equal(ExtensionNoPeriodFacts.TXT, modelModifier.FileExtension);
00442 
00443             // The text is small, it should write a single partition, nothing more.
00444             Assert.Single(modelModifier.PartitionList);
00445 
00446             // 1 tab key was included in the initial content for the TextEditorModel but,
00447             // now that the content is cleared, the Count is 0.
00448             Assert.Equal(0, modelModifier.TabKeyPositionList.Count);
00449 
00450             // LineEnd related code-block-grouping:
00451             {
00452                 // 1 CarriageReturn was included in the initial content for the TextEditorModel but,
00453                 // after clearing the content, the count is 0.
00454                 Assert.Equal(
00455                     0,
00456                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturn).count);
00457 
00458                 // 1 LineFeed was included in the initial content for the TextEditorModel but,
00459                 // after clearing the content, the count is 0.
00460                 Assert.Equal(
00461                     0,
00462                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.LineFeed).count);
00463 
00464                 // 1 CarriageReturnLineFeed was included in the initial content for the TextEditorModel but,
00465                 // after clearing the content, the count is 0.
00466                 Assert.Equal(
00467                     0,
00468                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturnLineFeed).count);
00469 
00470                 // 3 line endings where included in the initial content for the TextEditorModel but,
00471                 // after clearing the content, only the special-'EndOfFile' LineEnd should remain, so the count is 1.
00472                 Assert.Equal(1, modelModifier.LineEndList.Count);
00473 
00474                 // A TextEditorModel always contains at least 1 LineEnd.
00475                 // This LineEnd marks the 'EndOfFile'.
00476                 //
00477                 // The constructor for 'TextEditorModel' takes the 'initialContent' and sets the model's content as it,
00478                 // this results in the 'EndOfFile' positionIndex changing.
00479                 // But, since the content was cleared, the 'EndOfFile' positionIndex should return to 0.
00480                 var endOfFile = modelModifier.LineEndList[0];
00481                 Assert.Equal(0, endOfFile.StartPositionIndexInclusive);
00482                 Assert.Equal(0, endOfFile.EndPositionIndexExclusive);
00483                 Assert.Equal(LineEndKind.EndOfFile, endOfFile.LineEndKind);
00484             }
00485         }
00486     }
