00650 
00651                 // 1 LineFeed was included in the initial content for the TextEditorModel but,
00652                 // after setting the content, the count is 0.
00653                 Assert.Equal(
00654                     0,
00655                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.LineFeed).count);
00656 
00657                 // 1 CarriageReturnLineFeed was included in the initial content for the TextEditorModel but,
