00871     public void SetContent_IncreaseCounters()
00872     {
00873         var (inModel, modelModifier) = NotEmptyEditor_TestData_And_PerformPreAssertions(
00874             resourceUri: new ResourceUri($"/{nameof(NotEmptyEditor_TestData_And_PerformPreAssertions)}.txt"),
00875             resourceLastWriteTime: DateTime.MinValue,
00876             fileExtension: ExtensionNoPeriodFacts.TXT,
00877             content:
00878                 (
00879                     "\n" +   // LineFeed
00880                     "b9" +   // LetterOrDigit-Lowercase
00881                     "\r" +   // CarriageReturn
00882                     "9B" +   // LetterOrDigit-Uppercase
00883                     "\r\n" + // CarriageReturnLineFeed
00884                     "\t" +   // Tab
00885                     "$" +    // SpecialCharacter
00886                     ";" +    // Punctuation
00887                     " "      // Space
00888                 ),
00889             decorationMapper: null,
00890             compilerService: null,
00891             partitionSize: 4096);
00892 
00893         // Do something
00894         TextEditorModel outModel;
00895         {
00896             modelModifier.SetContent(
00897                 "\n" +   // LineFeed
00898                 "\n" +   // LineFeed
00899                 "b9" +   // LetterOrDigit-Lowercase
00900                 "\r" +   // CarriageReturn
00901                 "\r" +   // CarriageReturn
00902                 "9B" +   // LetterOrDigit-Uppercase
00903                 "\r\n" + // CarriageReturnLineFeed
00904                 "\r\n" + // CarriageReturnLineFeed
00905                 "\t" +   // Tab
00906                 "\t" +   // Tab
00907                 "$" +    // SpecialCharacter
00908                 ";" +    // Punctuation
00909                 ";" +    // Punctuation
00910                 " "      // Space
00911             );
00912             outModel = modelModifier.ToModel();
00913         }
00914 
00915         // Post-assertions
00916         {
00917             // The 'SetContent' parameter has duplicated any characters which are tracked by the text editor:
00918             //     '\t'   // Tabs
00919             //     '\r'   // CarriageReturn is tracked because LineEnd(s) are tracked
00920             //     '\n'   // NewLine is tracked because LineEnd(s) are tracked
00921             //     '\r\n' // CarriageReturnNewLine is tracked because LineEnd(s) are tracked
00922             // Therefore, the DocumentLength is '18'.
00923             Assert.Equal(18, modelModifier.CharCount);
00924 
00925             // The file extension should NOT change as a result of setting the content.
00926             Assert.Equal(ExtensionNoPeriodFacts.TXT, modelModifier.FileExtension);
00927 
00928             // The text is small, it should write a single partition, nothing more.
00929             Assert.Single(modelModifier.PartitionList);
00930 
00931             // 1 tab key was included in the initial content for the TextEditorModel.
00932             //
00933             // For this test, any character which is "tracked" by the text editor,
00934             // has been duplicated.
00935             //
00936             // This will make the count for those "tracked" characters go up.
00937             //
00938             // So, since there was initially 1 tab key, then the tab keys were
00939             // duplicated, there are now 2 tab keys.
00940             Assert.Equal(2, modelModifier.TabKeyPositionList.Count);
00941             Assert.Equal(12, modelModifier.TabKeyPositionList[0]);
00942             Assert.Equal(13, modelModifier.TabKeyPositionList[1]);
00943 
00944             // LineEnd related code-block-grouping:
00945             {
00946                 // 1 CarriageReturn was included in the initial content for the TextEditorModel.
00947                 // LineEnd(s) are tracked, therefore this CarriageReturn was duplicated.
00948                 // Therefore, there are now 2 CarriageReturn(s).
00949                 Assert.Equal(
00950                     2,
00951                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturn).count);
00952                 // The first CarriageReturn is at positionIndex '4'. Therefore,
00953                 // get the single entry with a value of '4' from 'LineEndPositionList',
00954                 // and then assert that it is a CarriageReturn.
00955                 Assert.Equal(LineEndKind.CarriageReturn, modelModifier.LineEndList.Single(x => x.StartPositionIndexInclusive == 4).LineEndKind);
00956                 // The second CarriageReturn is at positionIndex '5'. Therefore,
00957                 // get the single entry with a value of '5' from 'LineEndPositionList',
00958                 // and then assert that it is a CarriageReturn.
00959                 Assert.Equal(LineEndKind.CarriageReturn, modelModifier.LineEndList.Single(x => x.StartPositionIndexInclusive == 5).LineEndKind);
00960 
00961                 // 1 LineFeed was included in the initial content for the TextEditorModel.
00962                 // LineEnd(s) are tracked, therefore this LineFeed was duplicated.
00963                 // Therefore, there are now 2 LineFeed(s).
00964                 Assert.Equal(
00965                     2,
00966                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.LineFeed).count);
00967                 // The first LineFeed is at positionIndex '0'. Therefore,
00968                 // get the single entry with a value of '0' from 'LineEndPositionList',
00969                 // and then assert that it is a LineFeed.
00970                 Assert.Equal(LineEndKind.LineFeed, modelModifier.LineEndList.Single(x => x.StartPositionIndexInclusive == 0).LineEndKind);
00971                 // The second LineFeed is at positionIndex '1'. Therefore,
00972                 // get the single entry with a value of '1' from 'LineEndPositionList',
00973                 // and then assert that it is a LineFeed.
00974                 Assert.Equal(LineEndKind.LineFeed, modelModifier.LineEndList.Single(x => x.StartPositionIndexInclusive == 1).LineEndKind);
00975 
00976                 // 1 CarriageReturnLineFeed was included in the initial content for the TextEditorModel.
00977                 // LineEnd(s) are tracked, therefore this CarriageReturnLineFeed was duplicated.
00978                 // Therefore, there are now 2 CarriageReturnLineFeed(s).
00979                 Assert.Equal(
00980                     2,
00981                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturnLineFeed).count);
00982                 // The first CarriageReturnLineFeed is at positionIndex '8'. Therefore,
00983                 // get the single entry with a value of '8' from 'LineEndPositionList',
00984                 // and then assert that it is a CarriageReturnLineFeed.
00985                 Assert.Equal(LineEndKind.CarriageReturnLineFeed, modelModifier.LineEndList.Single(x => x.StartPositionIndexInclusive == 8).LineEndKind);
00986                 // The second CarriageReturnLineFeed is at positionIndex '10'. Therefore,
00987                 // get the single entry with a value of '10' from 'LineEndPositionList',
00988                 // and then assert that it is a CarriageReturnLineFeed.
00989                 Assert.Equal(LineEndKind.CarriageReturnLineFeed, modelModifier.LineEndList.Single(x => x.StartPositionIndexInclusive == 10).LineEndKind);
00990 
00991                 // 3 line endings where included in the initial content for the TextEditorModel.
00992                 //
00993                 // All the LineEnd(s) were duplicated, therefore there are now 6-explicit line ends,
00994                 // and as well, the 1-implicit EndOfFile line end.
00995                 Assert.Equal(7, modelModifier.LineEndList.Count);
00996 
00997                 // A TextEditorModel always contains at least 1 LineEnd.
00998                 // This LineEnd marks the 'EndOfFile'.
00999                 //
01000                 // The constructor for 'TextEditorModel' takes the 'initialContent' and sets the model's content as it,
01001                 // this results in the 'EndOfFile' positionIndex changing.
01002                 // Since any tracked characters were duplicated,
01003                 // the EndOfFile would move by 4 characters:
01004                 //     '\t'   // Tabs
01005                 //     '\r'   // CarriageReturn is tracked because LineEnd(s) are tracked
01006                 //     '\n'   // NewLine is tracked because LineEnd(s) are tracked
01007                 //     '\r\n' // CarriageReturnNewLine is tracked because LineEnd(s) are tracked
01008                 var endOfFile = modelModifier.LineEndList.Last();
01009                 Assert.Equal(LineEndKind.EndOfFile, endOfFile.LineEndKind);
01010                 Assert.Equal(18, endOfFile.StartPositionIndexInclusive);
01011                 Assert.Equal(18, endOfFile.EndPositionIndexExclusive);
01012             }
01013         }
