01125     [Fact]
01126     public void PartitionList_Add_SHOULD_INSERT_INTO_PARTITION_WITH_AVAILABLE_SPACE()
01127     {
01128         var fileExtension = ExtensionNoPeriodFacts.TXT;
01129         var resourceUri = new ResourceUri("/unitTesting.txt");
01130         var resourceLastWriteTime = DateTime.UtcNow;
01131 
01132         var model = new TextEditorModel(
01133              resourceUri,
01134              resourceLastWriteTime,
01135              fileExtension,
01136              string.Empty,
01137              null,
01138              null,
01139              partitionSize: 5);
01140 
01141         var modifier = new TextEditorModelModifier(model);
01142 
01143         // Assert that the first partition is empty at the start.
01144         Assert.Empty(modifier.PartitionList.First().RichCharacterList);
01145 
01146         // Assert that more space than just one partition will be needed.
01147         var sourceText = "Hello World!";
01148         Assert.True(sourceText.Length > model.PartitionSize);
01149 
01150         var firstPartitionStringValue = new string(modifier.PartitionList.First().RichCharacterList.Select(x => x.Value).ToArray());
01151 
01152         for (int i = 0; i < sourceText.Length; i++)
01153         {
01154             var firstPartition = modifier.PartitionList.First();
01155 
01156             var richCharacter = new RichCharacter(sourceText[i], default);
01157             modifier.__Add(richCharacter);
01158 
01159             if (i < model.PartitionSize)
01160             {
01161                 // Assert that the first n loops write to the first partition, because it has available space
01162                 // This is asserted by checking that the string value of the first partition has changed.
01163                 var newStringValue = new string(modifier.PartitionList.First().RichCharacterList.Select(x => x.Value).ToArray());
01164                 Assert.NotEqual(firstPartitionStringValue, newStringValue);
01165                 firstPartitionStringValue = newStringValue;
01166             }
01167             else
01168             {
01169                 // Assert that the last (n + 1) loops do NOT write to the first partition, because it no longer has available space
01170                 // This is asserted by checking that the string value of the first partition has NOT changed.
01171                 var newStringValue = new string(modifier.PartitionList.First().RichCharacterList.Select(x => x.Value).ToArray());
01172                 Assert.Equal(firstPartitionStringValue, newStringValue);
01173             }
01174         }
01175 
01176         // Assert that the output is correct.
01177         Assert.Equal(
01178             new string(modifier.RichCharacterList.Select(x => x.Value).ToArray()),
01179             sourceText);
01180     }
