01182     [Fact]
01183     public void PartitionList_Add_SHOULD_CREATE_MORE_SPACE_IF_NEEDED_V2()
01184     {
01185         var fileExtension = ExtensionNoPeriodFacts.TXT;
01186         var resourceUri = new ResourceUri("/unitTesting.txt");
01187         var resourceLastWriteTime = DateTime.UtcNow;
01188 
01189         var model = new TextEditorModel(
01190              resourceUri,
01191              resourceLastWriteTime,
01192              fileExtension,
01193              string.Empty,
01194              null,
01195              null,
01196              partitionSize: 5);
01197 
01198         var modifier = new TextEditorModelModifier(model);
01199 
01200         // Assert that only one partition exists at the start.
01201         Assert.Single(modifier.PartitionList);
01202 
01203         // Assert that more space will be needed.
01204         var sourceText = "Hello World!";
01205         Assert.True(sourceText.Length > model.PartitionSize);
01206 
01207         for (int i = 0; i < sourceText.Length; i++)
01208         {
01209             if (i == model.PartitionSize)
01210             {
01211                 // Assert that up until this loop iteration only 1 partition has existed.
01212                 Assert.Single(modifier.PartitionList);
01213             }
01214 
01215             var richCharacter = new RichCharacter(sourceText[i], default);
01216             modifier.__Add(richCharacter);
01217 
01218             if (i == model.PartitionSize)
01219             {
01220                 // Assert that this loop iteration caused another partition to be made
01221                 Assert.Equal(2, modifier.PartitionList.Count);
01222 
01223                 // Furthermore, assert that the first partition contains
01224                 // (PARTITION_SIZE / 2 + (PARTITION_SIZE % 2)) entries, and the second partition contains
01225                 // (PARTITION_SIZE / 2)
01226                 Assert.Equal(
01227                     model.PartitionSize / 2 + model.PartitionSize % 2,
01228                     modifier.PartitionList.First().RichCharacterList.Count);
01229 
01230                 Assert.Equal(
01231                     // This had to be changed to include a '+1' because the insertion already occurred.
01232                     model.PartitionSize / 2 + 1,
01233                     modifier.PartitionList.Last().RichCharacterList.Count);
01234             }
01235         }
01236 
01237         Assert.Equal(
01238             "Hel",
01239             new string(modifier.PartitionList[0].RichCharacterList.Select(x => x.Value).ToArray()));
01240 
01241         Assert.Equal(
01242             "lo ",
01243             new string(modifier.PartitionList[1].RichCharacterList.Select(x => x.Value).ToArray()));
01244 
01245         Assert.Equal(
01246             "Wor",
01247             new string(modifier.PartitionList[2].RichCharacterList.Select(x => x.Value).ToArray()));
01248 
01249         Assert.Equal(
01250             "ld!",
01251             new string(modifier.PartitionList[3].RichCharacterList.Select(x => x.Value).ToArray()));
01252 
01253         // Assert that the output is correct.
01254         Assert.Equal(
01255             new string(modifier.RichCharacterList.Select(x => x.Value).ToArray()),
01256             sourceText);
01257     }
