00168     public void WriteToStorage()
00169     {
00170         _commonBackgroundTaskApi.Storage.WriteToLocalStorage(
00171             _textEditorService.StorageKey,
00172             new TextEditorOptionsJsonDto(_textEditorService.OptionsStateWrap.Value.Options));
00173     }
