00175     public async Task SetFromLocalStorageAsync()
00176     {
00177         var optionsJsonString = await _storageService.GetValue(_textEditorService.StorageKey).ConfigureAwait(false) as string;
00178 
00179         if (string.IsNullOrWhiteSpace(optionsJsonString))
00180             return;
00181 
00182         var optionsJson = JsonSerializer.Deserialize<TextEditorOptionsJsonDto>(optionsJsonString);
00183 
00184         if (optionsJson is null)
00185             return;
00186 
00187         if (optionsJson.CommonOptionsJsonDto?.ThemeKey is not null)
00188         {
00189             var matchedTheme = _textEditorService.ThemeStateWrap.Value.ThemeList.FirstOrDefault(
00190                 x => x.Key == optionsJson.CommonOptionsJsonDto.ThemeKey);
00191 
00192             SetTheme(matchedTheme ?? ThemeFacts.VisualStudioDarkThemeClone, false);
00193         }
00194 
00195         if (optionsJson.Keymap is not null)
00196         {
00197             var matchedKeymap = TextEditorKeymapFacts.AllKeymapsList.FirstOrDefault(
00198                 x => x.Key == optionsJson.Keymap.Key);
00199 
00200             SetKeymap(matchedKeymap ?? TextEditorKeymapFacts.DefaultKeymap, false);
00201 
00202             var activeKeymap = _textEditorService.OptionsStateWrap.Value.Options.Keymap;
00203 
00204             if (activeKeymap is not null)
00205             {
00206                 _dispatcher.Dispatch(new SetContextKeymapAction(
00207                     ContextFacts.TextEditorContext.ContextKey,
00208                     activeKeymap));
00209             }
00210         }
00211 
00212         if (optionsJson.CommonOptionsJsonDto?.FontSizeInPixels is not null)
00213             SetFontSize(optionsJson.CommonOptionsJsonDto.FontSizeInPixels.Value, false);
00214 
00215         if (optionsJson.CursorWidthInPixels is not null)
00216             SetCursorWidth(optionsJson.CursorWidthInPixels.Value, false);
00217 
00218         if (optionsJson.TextEditorHeightInPixels is not null)
00219             SetHeight(optionsJson.TextEditorHeightInPixels.Value, false);
00220 
00221         if (optionsJson.ShowNewlines is not null)
00222             SetShowNewlines(optionsJson.ShowNewlines.Value, false);
00223 
00224         // TODO: OptionsSetUseMonospaceOptimizations will always get set to false (default for bool)
00225         // for a first time user. This leads to a bad user experience since the proportional
00226         // font logic is still being optimized. Therefore don't read in UseMonospaceOptimizations
00227         // from local storage.
00228         //
00229         // OptionsSetUseMonospaceOptimizations(options.UseMonospaceOptimizations);
00230 
00231         if (optionsJson.ShowWhitespace is not null)
00232             SetShowWhitespace(optionsJson.ShowWhitespace.Value, false);
00233     }
