00357     private void InitializeOptionsApiTests(
00358         out ITextEditorService textEditorService,
00359         out IState<TextEditorOptionsState> textEditorOptionsState,
00360         out IDispatcher dispatcher,
00361         out ServiceProvider serviceProvider)
00362     {
00363         var backgroundTaskService = new BackgroundTaskServiceSynchronous();
00364 
00365         var serviceCollection = new ServiceCollection()
00366             .AddScoped<IJSRuntime, DoNothingJsRuntime>()
00367             .AddLuthetusTextEditor(new LuthetusHostingInformation(
00368                 LuthetusHostingKind.UnitTestingSynchronous,
00369                 LuthetusPurposeKind.TextEditor,
00370                 backgroundTaskService))
00371             .AddFluxor(options => options.ScanAssemblies(
00372                 typeof(LuthetusCommonConfig).Assembly,
00373                 typeof(LuthetusTextEditorConfig).Assembly));
00374 
00375         serviceProvider = serviceCollection.BuildServiceProvider();
00376 
00377         var store = serviceProvider.GetRequiredService<IStore>();
00378         store.InitializeAsync().Wait();
00379 
00380         dispatcher = serviceProvider.GetRequiredService<IDispatcher>();
00381         textEditorService = serviceProvider.GetRequiredService<ITextEditorService>();
00382         textEditorOptionsState = serviceProvider.GetRequiredService<IState<TextEditorOptionsState>>();
00383     }
