00169     public async Task FinalizePost(ITextEditorEditContext editContext)
00170     {
00171         var modelModifierNeedRenderList = new List<TextEditorModelModifier>();
00172         var viewModelModifierNeedRenderList = new List<TextEditorViewModelModifier>();
00173 
00174         foreach (var modelModifier in editContext.ModelCache.Values)
00175         {
00176             if (modelModifier is null || !modelModifier.WasModified)
00177                 continue;
00178 
00179             modelModifierNeedRenderList.Add(modelModifier);
00180 
00181             var viewModelBag = editContext.TextEditorService.ModelApi.GetViewModelsOrEmpty(modelModifier.ResourceUri);
00182 
00183             foreach (var viewModel in viewModelBag)
00184             {
00185                 // Invoking 'GetViewModelModifier' marks the view model to be updated.
00186                 var viewModelModifier = editContext.GetViewModelModifier(viewModel.ViewModelKey);
00187 
00188                 if (!viewModelModifier.ShouldReloadVirtualizationResult)
00189                     viewModelModifier.ShouldReloadVirtualizationResult = modelModifier.ShouldReloadVirtualizationResult;
00190             }
00191 
00192             if (modelModifier.WasDirty != modelModifier.IsDirty)
00193             {
00194                 if (modelModifier.IsDirty)
00195                     _dispatcher.Dispatch(new DirtyResourceUriState.AddDirtyResourceUriAction(modelModifier.ResourceUri));
00196                 else
00197                     _dispatcher.Dispatch(new DirtyResourceUriState.RemoveDirtyResourceUriAction(modelModifier.ResourceUri));
00198             }
00199         }
00200 
00201         foreach (var viewModelModifier in editContext.ViewModelCache.Values)
00202         {
00203             if (viewModelModifier is null || !viewModelModifier.WasModified)
00204                 return;
00205 
00206             viewModelModifierNeedRenderList.Add(viewModelModifier);
00207             
00208             var successCursorModifierBag = editContext.CursorModifierBagCache.TryGetValue(
00209                 viewModelModifier.ViewModel.ViewModelKey,
00210                 out var cursorModifierBag);
00211 
00212             if (successCursorModifierBag && cursorModifierBag is not null)
00213             {
00214                 viewModelModifier.ViewModel = viewModelModifier.ViewModel with
00215                 {
00216                     CursorList = cursorModifierBag.List
00217                         .Select(x => x.ToCursor())
00218                         .ToImmutableArray()
00219                 };
00220             }
00221             
00222             if (viewModelModifier.ViewModel.UnsafeState.ShouldRevealCursor)
00223             {
00224                 var modelModifier = editContext.GetModelModifier(viewModelModifier.ViewModel.ResourceUri);
00225                 cursorModifierBag ??= editContext.GetCursorModifierBag(viewModelModifier.ViewModel);
00226                 var cursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00227                 
00228                 if (modelModifier is not null)
00229                 {
00230                     ViewModelApi.RevealCursor(
00231                         editContext,
00232                         modelModifier,
00233                         viewModelModifier,
00234                         cursorModifierBag,
00235                         cursorModifier);
00236                 }
00237             }
00238             
00239             // This if expression exists below, to check if 'CalculateVirtualizationResult(...)' should be invoked.
00240             //
00241             // But, note that these cannot be combined at the bottom, we need to check if an edit
00242             // reduced the scrollWidth or scrollHeight of the editor's content.
00243             // 
00244             // This is done here, so that the 'ScrollWasModified' bool can be set, and downstream if statements will be entered,
00245             // which go on to scroll the editor.
00246             if (viewModelModifier.ShouldReloadVirtualizationResult)
00247             {
00248                 ValidateMaximumScrollLeftAndScrollTop(editContext, viewModelModifier, textEditorDimensionsChanged: false);
00249             }
00250 
00251             if (viewModelModifier.ScrollWasModified)
00252             {
00253                 // TODO: If the API is self validating then this explicit final validation wouldn't be needed?
00254                 /*
00255                 var validateScrollbarDimensions = viewModelModifier.ViewModel.ScrollbarDimensions;
00256             
00257                 validateScrollbarDimensions = viewModelModifier.ViewModel.ScrollbarDimensions.WithSetScrollLeft(
00258                     (int)validateScrollbarDimensions.ScrollLeft,
00259                     viewModelModifier.ViewModel.TextEditorDimensions);
00260                         
00261                 validateScrollbarDimensions = validateScrollbarDimensions.WithSetScrollTop(
00262                     (int)validateScrollbarDimensions.ScrollTop,
00263                     viewModelModifier.ViewModel.TextEditorDimensions);
00264                     
00265                 viewModelModifier.ViewModel = viewModelModifier.ViewModel with
00266                 {
00267                     ScrollbarDimensions = validateScrollbarDimensions
00268                 };
00269                 */
00270             
00271                 await JsRuntimeTextEditorApi
00272                     .SetScrollPosition(
00273                         viewModelModifier.ViewModel.BodyElementId,
00274                         viewModelModifier.ViewModel.GutterElementId,
00275                         viewModelModifier.ViewModel.ScrollbarDimensions.ScrollLeft,
00276                         viewModelModifier.ViewModel.ScrollbarDimensions.ScrollTop)
00277                     .ConfigureAwait(false);
00278             }
00279             
00280             if (!viewModelModifier.ShouldReloadVirtualizationResult &&
00281                 viewModelModifier.ScrollWasModified)
00282             {
00283                 // If not already going to reload virtualization result,
00284                 // then check if the virtualization needs to be refreshed due to a
00285                 // change in scroll position.
00286                 //
00287                 // This code only needs to run if the scrollbar was modified.
00288                 
00289                 if (viewModelModifier.ViewModel.VirtualizationResult.EntryList.Length > 0)
00290                 {
00291                     var firstEntry = viewModelModifier.ViewModel.VirtualizationResult.EntryList.First();
00292                     var firstEntryTop = firstEntry.Index * viewModelModifier.ViewModel.CharAndLineMeasurements.LineHeight;
00293                     
00294                     if (viewModelModifier.ViewModel.ScrollbarDimensions.ScrollTop < firstEntryTop)
00295                     {
00296                         viewModelModifier.ShouldReloadVirtualizationResult = true;
00297                     }
00298                     else
00299                     {
00300                         var bigTop = viewModelModifier.ViewModel.ScrollbarDimensions.ScrollTop +
00301                             viewModelModifier.ViewModel.TextEditorDimensions.Height;
00302                             
00303                         var imaginaryLastEntry = viewModelModifier.ViewModel.VirtualizationResult.EntryList.Last();
00304                         var imaginaryLastEntryTop = (imaginaryLastEntry.Index + 1) * viewModelModifier.ViewModel.CharAndLineMeasurements.LineHeight;
00305                             
00306                         if (bigTop > imaginaryLastEntryTop)
00307                             viewModelModifier.ShouldReloadVirtualizationResult = true;
00308                     }
00309                 }
00310                 
00311                 // A check for horizontal virtualization still needs to be done.
00312                 //
00313                 // If we didn't already determine the necessity of calculating the virtualization
00314                 // result when checking the vertical virtualization, then we check horizontal.
00315                 if (!viewModelModifier.ShouldReloadVirtualizationResult)
00316                 {
00317                     // low end plus width of it
00318                 
00319                     var leftBoundary = viewModelModifier.ViewModel.VirtualizationResult.LeftVirtualizationBoundary;
00320                     var scrollLeft = viewModelModifier.ViewModel.ScrollbarDimensions.ScrollLeft;
00321                     
00322                     if (scrollLeft < (leftBoundary.LeftInPixels + leftBoundary.WidthInPixels))
00323                     {
00324                         viewModelModifier.ShouldReloadVirtualizationResult = true;
00325                     }
00326                     else
00327                     {
00328                         var rightBoundary = viewModelModifier.ViewModel.VirtualizationResult.RightVirtualizationBoundary;
00329                         var bigLeft = scrollLeft + viewModelModifier.ViewModel.TextEditorDimensions.Width;
00330                         
00331                         if (bigLeft > rightBoundary.LeftInPixels)
00332                         {
00333                             viewModelModifier.ShouldReloadVirtualizationResult = true;
00334                         }
00335                     }
00336                 }
00337             }
00338 
00339             if (viewModelModifier.ShouldReloadVirtualizationResult)
00340             {           
00341                 // TODO: This 'CalculateVirtualizationResultFactory' invocation is horrible for performance.
00342                 editContext.TextEditorService.ViewModelApi.CalculateVirtualizationResult(
00343                     editContext,
00344                     editContext.GetModelModifier(viewModelModifier.ViewModel.ResourceUri),
00345                     viewModelModifier,
00346                     CancellationToken.None);
00347             }
00348             
00349             _dispatcher.Dispatch(new TextEditorState.SetModelAndViewModelRangeAction(
00350                 editContext.AuthenticatedActionKey,
00351                 editContext,
00352                 modelModifierNeedRenderList,
00353                 viewModelModifierNeedRenderList));
00354         }
00355     }
