00379     public void ValidateMaximumScrollLeftAndScrollTop(
00380         ITextEditorEditContext editContext,
00381         TextEditorViewModelModifier viewModelModifier,
00382         bool textEditorDimensionsChanged)
00383     {   
00384         var modelModifier = editContext.GetModelModifier(viewModelModifier.ViewModel.ResourceUri);
00385         
00386         if (modelModifier is null)
00387             return;
00388         
00389         var originalScrollWidth = viewModelModifier.ViewModel.ScrollbarDimensions.ScrollWidth;
00390         var originalScrollHeight = viewModelModifier.ViewModel.ScrollbarDimensions.ScrollHeight;
00391     
00392         var totalWidth = (int)Math.Ceiling(modelModifier.MostCharactersOnASingleLineTuple.lineLength *
00393             viewModelModifier.ViewModel.CharAndLineMeasurements.CharacterWidth);
00394 
00395         // Account for any tab characters on the 'MostCharactersOnASingleLineTuple'
00396         //
00397         // TODO: This code is not fully correct...
00398         //       ...if the longest line is 50 non-tab characters,
00399         //       and the second longest line is 49 tab characters,
00400         //       this code will erroneously take the '50' non-tab characters
00401         //       to be the longest line.
00402         {
00403             var lineIndex = modelModifier.MostCharactersOnASingleLineTuple.lineIndex;
00404             var longestLineInformation = modelModifier.GetLineInformation(lineIndex);
00405 
00406             var tabCountOnLongestLine = modelModifier.GetTabCountOnSameLineBeforeCursor(
00407                 longestLineInformation.Index,
00408                 longestLineInformation.LastValidColumnIndex);
00409 
00410             // 1 of the character width is already accounted for
00411             var extraWidthPerTabKey = TextEditorModel.TAB_WIDTH - 1;
00412 
00413             totalWidth += (int)Math.Ceiling(extraWidthPerTabKey *
00414                 tabCountOnLongestLine *
00415                 viewModelModifier.ViewModel.CharAndLineMeasurements.CharacterWidth);
00416         }
00417 
00418         var totalHeight = (int)Math.Ceiling(modelModifier.LineEndList.Count *
00419             viewModelModifier.ViewModel.CharAndLineMeasurements.LineHeight);
00420 
00421         // Add vertical margin so the user can scroll beyond the final line of content
00422         int marginScrollHeight;
00423         {
00424             var percentOfMarginScrollHeightByPageUnit = 0.4;
00425 
00426             marginScrollHeight = (int)Math.Ceiling(viewModelModifier.ViewModel.TextEditorDimensions.Height * percentOfMarginScrollHeightByPageUnit);
00427             totalHeight += marginScrollHeight;
00428         }
00429 
00430         viewModelModifier.ViewModel = viewModelModifier.ViewModel with
00431         {
00432             ScrollbarDimensions = viewModelModifier.ViewModel.ScrollbarDimensions with
00433             {
00434                 ScrollWidth = totalWidth,
00435                 ScrollHeight = totalHeight,
00436                 MarginScrollHeight = marginScrollHeight
00437             },
00438         };
00439         
00440         var validateScrollbarDimensions = viewModelModifier.ViewModel.ScrollbarDimensions;
00441         
00442         if (originalScrollWidth > viewModelModifier.ViewModel.ScrollbarDimensions.ScrollWidth ||
00443             textEditorDimensionsChanged)
00444         {
00445             validateScrollbarDimensions = viewModelModifier.ViewModel.ScrollbarDimensions.WithSetScrollLeft(
00446                 (int)validateScrollbarDimensions.ScrollLeft,
00447                 viewModelModifier.ViewModel.TextEditorDimensions);
00448         }
00449         
00450         if (originalScrollHeight > viewModelModifier.ViewModel.ScrollbarDimensions.ScrollHeight ||
00451             textEditorDimensionsChanged)
00452         {
00453             validateScrollbarDimensions = validateScrollbarDimensions.WithSetScrollTop(
00454                 (int)validateScrollbarDimensions.ScrollTop,
00455                 viewModelModifier.ViewModel.TextEditorDimensions);
00456             
00457             // The scrollLeft currently does not have any margin. Therefore subtracting the margin isn't needed.
00458             //
00459             // For scrollTop however, if one does not subtract the MarginScrollHeight in the case of
00460             // 'textEditorDimensionsChanged'
00461             //
00462             // Then a "void" will render at the top portion of the text editor, seemingly the size
00463             // of the MarginScrollHeight.
00464             if (textEditorDimensionsChanged &&
00465                 viewModelModifier.ViewModel.ScrollbarDimensions.ScrollTop != validateScrollbarDimensions.ScrollTop)
00466             {
00467                 validateScrollbarDimensions = validateScrollbarDimensions.WithSetScrollTop(
00468                     (int)validateScrollbarDimensions.ScrollTop - (int)validateScrollbarDimensions.MarginScrollHeight,
00469                     viewModelModifier.ViewModel.TextEditorDimensions);
00470             }
00471         }
00472         
00473         var changeOccurred =
00474             viewModelModifier.ViewModel.ScrollbarDimensions.ScrollLeft != validateScrollbarDimensions.ScrollLeft ||
00475             viewModelModifier.ViewModel.ScrollbarDimensions.ScrollTop != validateScrollbarDimensions.ScrollTop;
00476         
00477         if (changeOccurred)
00478         {
00479             viewModelModifier.ScrollWasModified = true;
00480             
00481             viewModelModifier.ViewModel = viewModelModifier.ViewModel with
00482             {
00483                 ScrollbarDimensions = validateScrollbarDimensions
00484             };
00485         }
00486     }
