00488     public async Task OpenInEditorAsync(
00489         string absolutePath,
00490         bool shouldSetFocusToEditor,
00491         int? cursorPositionIndex,
00492         Category category,
00493         Key<TextEditorViewModel> preferredViewModelKey)
00494     {
00495         try
00496         {
00497             var resourceUri = new ResourceUri(absolutePath);
00498             var actualViewModelKey = await CommonLogic_OpenInEditorAsync(
00499                 resourceUri,
00500                 shouldSetFocusToEditor,
00501                 category,
00502                 preferredViewModelKey);
00503                 
00504             // Move cursor
00505             if (cursorPositionIndex is null)
00506                 return; // Leave the cursor unchanged if the argument is null
00507             PostUnique(nameof(OpenInEditorAsync), editContext =>
00508             {
00509                 var modelModifier = editContext.GetModelModifier(resourceUri);
00510                 var viewModelModifier = editContext.GetViewModelModifier(actualViewModelKey);
00511                 var cursorModifierBag = editContext.GetCursorModifierBag(viewModelModifier?.ViewModel);
00512                 var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00513         
00514                 if (modelModifier is null || viewModelModifier is null || cursorModifierBag is null || primaryCursorModifier is null)
00515                     return Task.CompletedTask;
00516             
00517                 var lineAndColumnIndices = modelModifier.GetLineAndColumnIndicesFromPositionIndex(cursorPositionIndex.Value);
00518                     
00519                 primaryCursorModifier.LineIndex = lineAndColumnIndices.lineIndex;
00520                 primaryCursorModifier.ColumnIndex = lineAndColumnIndices.columnIndex;
00521                 
00522                 viewModelModifier.ViewModel.UnsafeState.ShouldRevealCursor = true;
00523                 return Task.CompletedTask;
00524             });
00525         }
00526         catch (Exception e)
00527         {
00528             Console.WriteLine(e);
00529             // One would never want a failed attempt at opening a text file to cause a fatal exception.
00530             // TODO: Perhaps add a notification? Perhaps 'throw' then add handling in the callers? But again, this should never cause a fatal exception.
00531         }
00532     }
