00534     public async Task OpenInEditorAsync(
00535         string absolutePath,
00536         bool shouldSetFocusToEditor,
00537         int? lineIndex,
00538         int? columnIndex,
00539         Category category,
00540         Key<TextEditorViewModel> preferredViewModelKey)
00541     {
00542         try
00543         {
00544             // Standardize Resource Uri
00545             if (TextEditorConfig.AbsolutePathStandardizeFunc is null)
00546                 return;
00547                 
00548             var standardizedFilePathString = await TextEditorConfig.AbsolutePathStandardizeFunc
00549                 .Invoke(absolutePath, _serviceProvider)
00550                 .ConfigureAwait(false);
00551                 
00552             var resourceUri = new ResourceUri(standardizedFilePathString);
00553 
00554             var actualViewModelKey = await CommonLogic_OpenInEditorAsync(
00555                 resourceUri,
00556                 shouldSetFocusToEditor,
00557                 category,
00558                 preferredViewModelKey);
00559                 
00560             // Move cursor
00561             if (lineIndex is null && columnIndex is null)
00562                 return; // Leave the cursor unchanged if the argument is null
00563             PostUnique(nameof(OpenInEditorAsync), editContext =>
00564             {
00565                 var modelModifier = editContext.GetModelModifier(resourceUri);
00566                 var viewModelModifier = editContext.GetViewModelModifier(actualViewModelKey);
00567                 var cursorModifierBag = editContext.GetCursorModifierBag(viewModelModifier?.ViewModel);
00568                 var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00569         
00570                 if (modelModifier is null || viewModelModifier is null || cursorModifierBag is null || primaryCursorModifier is null)
00571                     return Task.CompletedTask;
00572             
00573                 if (lineIndex is not null)
00574                     primaryCursorModifier.LineIndex = lineIndex.Value;
00575                 if (columnIndex is not null)
00576                     primaryCursorModifier.ColumnIndex = columnIndex.Value;
00577                 
00578                 if (primaryCursorModifier.LineIndex > modelModifier.LineCount - 1)
00579                     primaryCursorModifier.LineIndex = modelModifier.LineCount - 1;
00580                 
00581                 var lineInformation = modelModifier.GetLineInformation(primaryCursorModifier.LineIndex);
00582                 
00583                 if (primaryCursorModifier.ColumnIndex > lineInformation.LastValidColumnIndex)
00584                     primaryCursorModifier.SetColumnIndexAndPreferred(lineInformation.LastValidColumnIndex);
00585                     
00586                 viewModelModifier.ViewModel.UnsafeState.ShouldRevealCursor = true;
00587                 
00588                 return Task.CompletedTask;
00589             });
00590         }
00591         catch (Exception e)
00592         {
00593             Console.WriteLine(e);
00594             // One would never want a failed attempt at opening a text file to cause a fatal exception.
00595             // TODO: Perhaps add a notification? Perhaps 'throw' then add handling in the callers? But again, this should never cause a fatal exception.
00596         }
00597     }
