00609         // RegisterModelFunc
00610         if (TextEditorConfig.RegisterModelFunc is null)
00611             return Key<TextEditorViewModel>.Empty;
00612         await TextEditorConfig.RegisterModelFunc.Invoke(new RegisterModelArgs(resourceUri, _serviceProvider)).ConfigureAwait(false);
00613     
00614         // TryRegisterViewModelFunc
00615         if (TextEditorConfig.TryRegisterViewModelFunc is null)
00616             return Key<TextEditorViewModel>.Empty;
00617         var actualViewModelKey = await TextEditorConfig.TryRegisterViewModelFunc.Invoke(new TryRegisterViewModelArgs(
00618             preferredViewModelKey, resourceUri, category, shouldSetFocusToEditor, _serviceProvider)).ConfigureAwait(false);
00619     
00620         // TryShowViewModelFunc
00621         if (actualViewModelKey == Key<TextEditorViewModel>.Empty || TextEditorConfig.TryShowViewModelFunc is null)
00622             return Key<TextEditorViewModel>.Empty;
00623         await TextEditorConfig.TryShowViewModelFunc.Invoke(new TryShowViewModelArgs(
00624             actualViewModelKey, Key<TextEditorGroup>.Empty, shouldSetFocusToEditor, _serviceProvider)).ConfigureAwait(false);
00625         
00626         return actualViewModelKey;
00627     }
00628 }
