00038     protected (
00039         BackgroundTaskService backgroundTaskService,
00040         ContinuousBackgroundTaskWorker backgroundTaskWorker,
00041         IDispatcher dispatcher,
00042         ITextEditorService textEditorService) InitializeBackgroundTasks()
00043     {
00044         var backgroundTaskService = new BackgroundTaskService();
00045         var hostingInformation = new LuthetusHostingInformation(
00046             LuthetusHostingKind.UnitTestingSynchronous,
00047             LuthetusPurposeKind.TextEditor,
00048             backgroundTaskService);
00049 
00050         var services = new ServiceCollection()
00051             .AddScoped<ILoggerFactory, NullLoggerFactory>()
00052             .AddScoped<IJSRuntime, DoNothingJsRuntime>()
00053             .AddLuthetusTextEditor(hostingInformation)
00054             .AddFluxor(options => options.ScanAssemblies(
00055                 typeof(LuthetusCommonConfig).Assembly,
00056                 typeof(LuthetusTextEditorConfig).Assembly));
00057 
00058         var serviceProvider = services.BuildServiceProvider();
00059 
00060         var store = serviceProvider.GetRequiredService<IStore>();
00061         store.InitializeAsync().Wait();
00062 
00063         var backgroundTaskWorker = serviceProvider.GetRequiredService<ContinuousBackgroundTaskWorker>();
00064 
00065         return (
00066             backgroundTaskService,
00067             backgroundTaskWorker,
00068             serviceProvider.GetRequiredService<IDispatcher>(),
00069             serviceProvider.GetRequiredService<ITextEditorService>());
00070     }
