00123 
00124         // Pre-assertions
00125         {
00126             // Obnoxiously write the constant value for the initialContent's length instead of capturing the TextEditorModel
00127             // constructor's 'initialContent' parameter, then checking '.Length'.
00128             //
00129             // This makes it more clear if the source text changes (accidentally or intentionally).
00130             // If one day this assertion fails, then someone touched the source text.
00131             Assert.Equal(0, modelModifier.CharCount);
00132 
00133             // The file extension should NOT change as a result of inserting content.
00134             Assert.Equal(ExtensionNoPeriodFacts.TXT, modelModifier.FileExtension);
00135 
00136             // The text is small, it should write a single partition, nothing more.
00137             Assert.Single(modelModifier.PartitionList);
00138 
00139             // No tab keys were included in the initial content for the TextEditorModel, therefore the Count is 0.
00140             Assert.Equal(0, modelModifier.TabKeyPositionList.Count);
00141 
00142             // LineEnd related code-block-grouping:
00143             {
00144                 // No CarriageReturns were included in the initial content for the TextEditorModel, therefore the count is 0.
00145                 Assert.Equal(
00146                     0,
00147                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturn).count);
00148 
00149                 // No LineFeeds were included in the initial content for the TextEditorModel, therefore the count is 0.
00150                 Assert.Equal(
00151                     0,
00152                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.LineFeed).count);
00153 
00154                 // No CarriageReturnLineFeeds were included in the initial content for the TextEditorModel, therefore the count is 0.
00155                 Assert.Equal(
00156                     0,
00157                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturnLineFeed).count);
00158 
00159                 // No line endings where included in the initial content for the TextEditorModel,
00160                 // there is only the always existing 'EndOfFile' line ending, therefore the Count is 1.
00161                 Assert.Equal(1, modelModifier.LineEndList.Count);
00162 
00163                 // When invoking the constructor for the TextEditorModel,
00164                 // an string.Empty was used, therefore there are no LineFeeds.
00165                 // Assert that the only line ending in the text is the 'EndOfFile'.
00166                 Assert.Equal(LineEndKind.EndOfFile, modelModifier.LineEndList.Single().LineEndKind);
00167 
00168                 // When invoking the constructor for the TextEditorModel,
00169                 // an string.Empty was used, therefore there are no CarriageReturns.
00170                 // Assert that the only line ending in the text is the 'EndOfFile'.
00171                 Assert.Equal(LineEndKind.EndOfFile, modelModifier.LineEndList.Single().LineEndKind);
00172 
00173                 // When invoking the constructor for the TextEditorModel,
00174                 // an string.Empty was used, therefore there are no CarriageReturnLineFeeds.
00175                 // Assert that the only line ending in the text is the 'EndOfFile'.
00176                 Assert.Equal(LineEndKind.EndOfFile, modelModifier.LineEndList.Single().LineEndKind);
00177 
00178                 // A TextEditorModel always contains at least 1 LineEnd. This LineEnd marks the 'EndOfFile'.
00179                 //
00180                 // The constructor for 'TextEditorModel' takes the 'initialContent'
00181                 // and sets the model's content as it, this results in the positionIndex for 'EndOfFile'
00182                 // to be shifted by the length of the 'initialContent'.
00183                 // But, the constructor was invoked with string.Empty, therefore,
00184                 // the 'EndOfFile' is unchanged.
00185                 var endOfFile = modelModifier.LineEndList.Single();
00186                 Assert.Equal(LineEndKind.EndOfFile, endOfFile.LineEndKind);
00187                 Assert.Equal(0, endOfFile.StartPositionIndexInclusive);
00188                 Assert.Equal(0, endOfFile.EndPositionIndexExclusive);
00189             }
00190         }
00191 
00192         return (inModel, modelModifier);
00193     }
