00281             Assert.Equal(
00282                 8,
00283                 modelModifier.TabKeyPositionList.Single());
00284 
00285             // LineEnd related code-block-grouping:
00286             {
00287                 // 1 CarriageReturn was included in the string that was passed to the constructor.
00288                 // NOTE: While the Insert(...) method does not allow '\r' or '\r\n', the constructor does.
00289                 Assert.Equal(
00290                     1,
00291                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturn).count);
00292                 {
00293                     var carriageReturn = modelModifier.LineEndList.Single(x => x.LineEndKind == LineEndKind.CarriageReturn);
00294                     // StartPositionIndexInclusive
00295                     Assert.Equal(
00296                         3,
00297                         carriageReturn.StartPositionIndexInclusive);
00298                     // EndPositionIndexExclusive
00299                     Assert.Equal(
00300                         4,
00301                         carriageReturn.EndPositionIndexExclusive);
00302                 }
00303 
00304                 // 1 LineFeed was included in the string that was passed to the constructor.
00305                 Assert.Equal(
00306                     1,
00307                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.LineFeed).count);
00308                 {
00309                     var lineFeed = modelModifier.LineEndList.Single(x => x.LineEndKind == LineEndKind.LineFeed);
00310                     // StartPositionIndexInclusive
00311                     Assert.Equal(
00312                         0,
00313                         lineFeed.StartPositionIndexInclusive);
00314                     // EndPositionIndexExclusive
00315                     Assert.Equal(
00316                         1,
00317                         lineFeed.EndPositionIndexExclusive);
00318                 }
00319 
00320                 // 1 CarriageReturnLineFeed was included in the string that was passed to the constructor.
00321                 // NOTE: While the Insert(...) method does not allow '\r' or '\r\n', the constructor does.
00322                 Assert.Equal(
00323                     1,
00324                     modelModifier.LineEndKindCountList.Single(x => x.lineEndKind == LineEndKind.CarriageReturnLineFeed).count);
00325                 {
00326                     var carriageReturnLineFeed = modelModifier.LineEndList.Single(x => x.LineEndKind == LineEndKind.CarriageReturnLineFeed);
00327                     // StartPositionIndexInclusive
00328                     Assert.Equal(
00329                         6,
00330                         carriageReturnLineFeed.StartPositionIndexInclusive);
00331                     // EndPositionIndexExclusive
00332                     Assert.Equal(
00333                         8,
00334                         carriageReturnLineFeed.EndPositionIndexExclusive);
00335                 }
00336 
00337                 // 3 line endings where included in the string that was passed to the constructor,
00338                 // There are 4 in total if one then includes the special-'EndOfFile' LineEnd.
00339                 Assert.Equal(4, modelModifier.LineEndList.Count);
00340 
00341                 // A TextEditorModel always contains at least 1 LineEnd. This LineEnd marks the 'EndOfFile'.
00342                 //
00343                 // The constructor for 'TextEditorModel' takes the 'initialContent'
