00156     protected override bool ShouldRender()
00157     {
00158         var shouldRender = true;
00159 
00160         if (_linkedViewModel is null)
00161             HandleTextEditorViewModelKeyChange();
00162 
00163         if (shouldRender)
00164             ConstructRenderBatch();
00165 
00166         if (_storedRenderBatchTuple.Unsafe.ViewModel is not null && _storedRenderBatchTuple.Unsafe.Options is not null)
00167         {
00168             var isFirstDisplay = _storedRenderBatchTuple.Unsafe.ViewModel.DisplayTracker.ConsumeIsFirstDisplay();
00169 
00170             var previousOptionsRenderStateKey = _previousRenderBatchTuple?.Unsafe?.Options?.RenderStateKey ?? Key<RenderState>.Empty;
00171             var currentOptionsRenderStateKey = _storedRenderBatchTuple.Unsafe.Options.RenderStateKey;
00172 
00173             if (previousOptionsRenderStateKey != currentOptionsRenderStateKey || isFirstDisplay)
00174             {
00175                 QueueRemeasureBackgroundTask(
00176                     _storedRenderBatchTuple.Unsafe,
00177                     MeasureCharacterWidthAndRowHeightElementId,
00178                     COUNT_OF_TEST_CHARACTERS,
00179                     CancellationToken.None);
00180             }
00181 
00182             if (isFirstDisplay)
00183                 QueueCalculateVirtualizationResultBackgroundTask(_storedRenderBatchTuple.Unsafe);
00184         }
00185         
00186         // Check if the gutter width changed. If so, re-measure text editor.
00187         var viewModel = _storedRenderBatchTuple.Validated?.ViewModel;
00188         var gutterWidthInPixels = _storedRenderBatchTuple.Validated?.GutterWidthInPixels;
00189         
00190         if (viewModel is not null && gutterWidthInPixels is not null)
00191         {
00192             if (_previousGutterWidthInPixels >= 0 && gutterWidthInPixels >= 0)
00193             {
00194                 var absoluteValueDifference = Math.Abs(_previousGutterWidthInPixels - gutterWidthInPixels.Value);
00195                 
00196                 if (absoluteValueDifference >= 0.5)
00197                 {
00198                     _previousGutterWidthInPixels = gutterWidthInPixels.Value;
00199                     viewModel.DisplayTracker.PostScrollAndRemeasure();
00200                 }
00201             }
00202         }
00203 
00204         return shouldRender;
00205     }
