00313     private void HandleTextEditorViewModelKeyChange()
00314     {
00315         lock (_linkedViewModelLock)
00316         {
00317             var localTextEditorViewModelKey = TextEditorViewModelKey;
00318 
00319             var nextViewModel = TextEditorStateWrap.Value.ViewModelGetOrDefault(localTextEditorViewModelKey);
00320 
00321             Key<TextEditorViewModel> nextViewModelKey;
00322 
00323             if (nextViewModel is null)
00324                 nextViewModelKey = Key<TextEditorViewModel>.Empty;
00325             else
00326                 nextViewModelKey = nextViewModel.ViewModelKey;
00327 
00328             var linkedViewModelKey = _linkedViewModel?.ViewModelKey ?? Key<TextEditorViewModel>.Empty;
00329             var viewKeyChanged = nextViewModelKey != linkedViewModelKey;
00330 
00331             if (viewKeyChanged)
00332             {
00333                 _linkedViewModel?.DisplayTracker.DecrementLinks();
00334                 nextViewModel?.DisplayTracker.IncrementLinks();
00335 
00336                 _linkedViewModel = nextViewModel;
00337 
00338                 if (nextViewModel is not null)
00339                     nextViewModel.UnsafeState.ShouldRevealCursor = true;
00340             }
00341         }
00342     }
