00476     private void ReceiveContentOnMouseMove(MouseEventArgs mouseEventArgs)
00477     {
00478         _userMouseIsInside = true;
00479 
00480         if ((mouseEventArgs.Buttons & 1) == 0)
00481             _componentData.ThinksLeftMouseButtonIsDown = false;
00482 
00483         var localThinksLeftMouseButtonIsDown = _componentData.ThinksLeftMouseButtonIsDown;
00484 
00485         var (model, viewModel) = TextEditorStateWrap.Value.GetModelAndViewModelOrDefault(
00486             TextEditorViewModelKey);
00487 
00488         var resourceUri = model?.ResourceUri ?? ResourceUri.Empty;
00489         var viewModelKey = viewModel?.ViewModelKey ?? Key<TextEditorViewModel>.Empty;
00490 
00491         if (resourceUri == ResourceUri.Empty ||
00492             viewModelKey == Key<TextEditorViewModel>.Empty)
00493         {
00494             return;
00495         }
00496 
00497         // MouseStoppedMovingEvent
00498         if (viewModel is not null)
00499         {
00500             // Hide the tooltip, if the user moves their cursor out of the tooltips UI.
00501             if (viewModel.TooltipViewModel is not null && _componentData.MouseNoLongerOverTooltipTask.IsCompleted)
00502             {
00503                 var mouseNoLongerOverTooltipCancellationToken = _componentData.MouseNoLongerOverTooltipCancellationTokenSource.Token;
00504 
00505                 _componentData.MouseNoLongerOverTooltipTask = Task.Run(async () =>
00506                 {
00507                     await Task.Delay(TextEditorComponentData.OnMouseOutTooltipDelay, mouseNoLongerOverTooltipCancellationToken).ConfigureAwait(false);
00508 
00509                     if (!mouseNoLongerOverTooltipCancellationToken.IsCancellationRequested)
00510                     {
00511                         TextEditorService.PostUnique(
00512                             nameof(ContextMenu),
00513                             editContext =>
00514                             {
00515                                 var viewModelModifier = editContext.GetViewModelModifier(viewModelKey);
00516 
00517                                 if (viewModelModifier is null)
00518                                     return Task.CompletedTask;
00519 
00520                                 viewModelModifier.ViewModel = viewModelModifier.ViewModel with 
00521                                 {
00522                                     TooltipViewModel = null
00523                                 };
00524 
00525                                 return Task.CompletedTask;
00526                             });
00527                     }
00528                 });
00529             }
00530 
00531             _componentData.MouseStoppedMovingCancellationTokenSource.Cancel();
00532             _componentData.MouseStoppedMovingCancellationTokenSource = new();
00533 
00534             var mouseStoppedMovingCancellationToken = _componentData.MouseStoppedMovingCancellationTokenSource.Token;
00535 
00536             _componentData.MouseStoppedMovingTask = Task.Run(async () =>
00537             {
00538                 await Task.Delay(TextEditorComponentData.MouseStoppedMovingDelay, mouseStoppedMovingCancellationToken).ConfigureAwait(false);
00539 
00540                 if (!mouseStoppedMovingCancellationToken.IsCancellationRequested && _userMouseIsInside)
00541                 {
00542                     await _componentData.ContinueRenderingTooltipAsync().ConfigureAwait(false);
00543 
00544                     TextEditorService.PostUnique(
00545                         nameof(TextEditorCommandDefaultFunctions.HandleMouseStoppedMovingEventAsync),
00546                         editContext =>
00547                         {
00548                             var modelModifier = editContext.GetModelModifier(resourceUri);
00549                             var viewModelModifier = editContext.GetViewModelModifier(viewModelKey);
00550                             var cursorModifierBag = editContext.GetCursorModifierBag(viewModelModifier?.ViewModel);
00551                             var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00552             
00553                             if (modelModifier is null || viewModelModifier is null || cursorModifierBag is null || primaryCursorModifier is null)
00554                                 return Task.CompletedTask;
00555                         
00556                             return TextEditorCommandDefaultFunctions.HandleMouseStoppedMovingEventAsync(
00557                                 editContext,
00558                                 modelModifier,
00559                                 viewModelModifier,
00560                                 mouseEventArgs,     
00561                                 _componentData,
00562                                 TextEditorComponentRenderers,
00563                                 resourceUri);
00564                         });
00565                 }
00566             });
00567         }
00568 
00569         if (!_componentData.ThinksLeftMouseButtonIsDown)
00570             return;
00571 
00572         if (resourceUri == ResourceUri.Empty ||
00573             viewModelKey == Key<TextEditorViewModel>.Empty)
00574         {
00575             return;
00576         }
00577 
00578         // Buttons is a bit flag '& 1' gets if left mouse button is held
00579         if (localThinksLeftMouseButtonIsDown && (mouseEventArgs.Buttons & 1) == 1)
00580         {
00581             var onMouseMove = new OnMouseMove(
00582                 mouseEventArgs,
00583                 _componentData,
00584                 resourceUri,
00585                 viewModelKey);
00586 
00587             TextEditorService.Post(onMouseMove);
00588         }
00589         else
00590         {
00591             _componentData.ThinksLeftMouseButtonIsDown = false;
00592         }
00593     }
