00657                     viewModelModifier,
00658                     diffX);
00659 
00660                 TextEditorService.ViewModelApi.MutateScrollVerticalPosition(
00661                     editContext,
00662                     viewModelModifier,
00663                     diffY);
00664                     
00665                 return Task.CompletedTask;
00666             });
00667 
00668         _previousTouchEventArgs = touchEventArgs;
