00671     private void ClearTouch(TouchEventArgs touchEventArgs)
00672     {
00673         var rememberStartTouchEventArgs = _previousTouchEventArgs;
00674 
00675         _thinksTouchIsOccurring = false;
00676         _previousTouchEventArgs = null;
00677 
00678         var clearTouchDateTime = DateTime.UtcNow;
00679         var touchTimespan = clearTouchDateTime - _touchStartDateTime;
00680 
00681         if (touchTimespan is null)
00682             return;
00683 
00684         if (touchTimespan.Value.TotalMilliseconds < 200)
00685         {
00686             var startTouchPoint = rememberStartTouchEventArgs?.ChangedTouches.FirstOrDefault(x => x.Identifier == 0);
00687 
00688             if (startTouchPoint is null)
00689                 return;
00690 
00691             ReceiveContentOnMouseDown(new MouseEventArgs
00692             {
00693                 Buttons = 1,
00694                 ClientX = startTouchPoint.ClientX,
00695                 ClientY = startTouchPoint.ClientY,
00696             });
00697         }
00698     }
