00700     private void QueueRemeasureBackgroundTask(
00701         ITextEditorRenderBatch localRefCurrentRenderBatch,
00702         string localMeasureCharacterWidthAndRowHeightElementId,
00703         int countOfTestCharacters,
00704         CancellationToken cancellationToken)
00705     {
00706         var (model, viewModel) = TextEditorStateWrap.Value.GetModelAndViewModelOrDefault(
00707             TextEditorViewModelKey);
00708 
00709         var resourceUri = model?.ResourceUri ?? ResourceUri.Empty;
00710         var viewModelKey = viewModel?.ViewModelKey ?? Key<TextEditorViewModel>.Empty;
00711 
00712         if (resourceUri == ResourceUri.Empty ||
00713             viewModelKey == Key<TextEditorViewModel>.Empty)
00714         {
00715             return;
00716         }
00717 
00718         TextEditorService.PostRedundant(
00719             nameof(QueueRemeasureBackgroundTask),
00720             resourceUri,
00721             viewModelKey,
00722             editContext =>
00723             {
00724                 var viewModelModifier = editContext.GetViewModelModifier(viewModelKey);
00725 
00726                 if (viewModelModifier is null)
00727                     return Task.CompletedTask;
00728 
00729                 return TextEditorService.ViewModelApi.RemeasureAsync(
00730                     editContext,
00731                     viewModelModifier,
00732                     localMeasureCharacterWidthAndRowHeightElementId,
00733                     countOfTestCharacters,
00734                     CancellationToken.None);
00735             });
00736     }
