00738     private void QueueCalculateVirtualizationResultBackgroundTask(
00739         ITextEditorRenderBatch localCurrentRenderBatch)
00740     {
00741         var (model, viewModel) = TextEditorStateWrap.Value.GetModelAndViewModelOrDefault(
