00053     protected override bool ShouldRender()
00054     {
00055         if (_previousTreeViewChangedKey != TreeViewNoType.TreeViewChangedKey)
00056         {
00057             _previousTreeViewChangedKey = TreeViewNoType.TreeViewChangedKey;
00058             return true;
00059         }
00060 
00061         return false;
00062     }
00063 
00064     protected override async Task OnAfterRenderAsync(bool firstRender)
00065     {
00066         var localIsActive = IsActive;
00067 
00068         if (_previousIsActive != localIsActive)
00069         {
00070             _previousIsActive = localIsActive;
00071 
00072             if (localIsActive)
00073                 await FocusAsync().ConfigureAwait(false);
00074         }
00075 
00076         await base.OnAfterRenderAsync(firstRender);
00077     }
00078 
00079     private async Task FocusAsync()
00080     {
00081         try
00082         {
00083             var localTreeViewTitleElementReference = _treeViewTitleElementReference;
00084 
00085             if (localTreeViewTitleElementReference is not null)
00086             {
00087                 await localTreeViewTitleElementReference.Value
00088                     .FocusAsync()
00089                     .ConfigureAwait(false);
00090             }
00091         }
00092         catch (Exception)
00093         {
00094             // 2023-04-18: The app has had a bug where it "freezes" and must be restarted.
00095             //             This bug is seemingly happening randomly. I have a suspicion
00096             //             that there are race-condition exceptions occurring with "FocusAsync"
00097             //             on an ElementReference.
00098         }
00099     }
